INSERT INTO `products` (`id`, `name`, `image`, `description`, `price`, `sale_price`, `slug`, `created`, `category_id`, `status`) VALUES
(6, 'CHUỐI TIÊU', 'chuoi.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">H&atilde;y để một ng&agrave;y của bạn trở n&ecirc;n thật v&agrave;ng tươi như những quả chuối n&agrave;y nh&eacute;! Sau đ&acirc;y l&agrave; 15 sự thật về loại tr&aacute;i c&acirc;y khiến ai cũng phải t&ograve; m&ograve; n&agrave;y!<br style="box-sizing: border-box;" />1. Chuối l&agrave; mặt h&agrave;ng b&aacute;n chạy nhất tại Walmart (tập đo&agrave;n b&aacute;n lẻ h&agrave;ng đầu thế giới)<br style="box-sizing: border-box;" />&ldquo;Gi&aacute; rẻ, vị ngon, lại tốt cho sức khỏe &ndash; đ&acirc;y hẳn l&agrave; sự lựa chọn đ&uacute;ng đắn d&agrave;nh cho bạn !&rdquo;<br style="box-sizing: border-box;" />ảnh chuối v&agrave;ng,sức khỏe,giảm căng thẳng,trượt vỏ chuối,quả chuối,tr&aacute;i c&acirc;y<br style="box-sizing: border-box;" />2. Chuối c&oacute; chứa một chất h&oacute;a học tự nhi&ecirc;n c&oacute; thể l&agrave;m cho con người ta trở n&ecirc;n hạnh ph&uacute;c - &nbsp;loại chất n&agrave;y được t&igrave;m thấy trong Prozac (t&ecirc;n một loại thuốc chống suy nhược)<br style="box-sizing: border-box;" />H&atilde;y d&agrave;nh thời gian thư gi&atilde;n v&agrave; tận hưởng 2 quả chuối thơm ngon mỗi ng&agrave;y nh&eacute; !<br style="box-sizing: border-box;" />ảnh chuối v&agrave;ng,sức khỏe,giảm căng thẳng,trượt vỏ chuối,quả chuối,tr&aacute;i c&acirc;y<br style="box-sizing: border-box;" />3. Ăn 50 quả chuối c&oacute; thể cung cấp cho bạn c&ugrave;ng lượng bức xạ như 1 tia X trong răng.<br style="box-sizing: border-box;" />Chuối c&oacute; thể được sử dụng như một đơn vị đo lường độ ph&oacute;ng xạ v&igrave; bản th&acirc;n ch&uacute;ng cũng c&oacute; chứa c&aacute;c đồng vị ph&oacute;ng xạ.<br style="box-sizing: border-box;" />ảnh chuối v&agrave;ng,sức khỏe,giảm căng thẳng,trượt vỏ chuối,quả chuối,tr&aacute;i c&acirc;y<br style="box-sizing: border-box;" />4. Chỉ cần 2 quả chuối c&oacute; thể cung cấp đủ năng lượng cho 90 ph&uacute;t tập luyện căng thẳng.<br style="box-sizing: border-box;" />Bạn c&oacute; nhớ v&igrave; sao lo&agrave;i vượn lại c&oacute; sức dẻo dai kh&ocirc;ng ?<br style="box-sizing: border-box;" />ảnh chuối v&agrave;ng,sức khỏe,giảm căng thẳng,trượt vỏ chuối,quả chuối,tr&aacute;i c&acirc;y</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/chuoi-2.jpg?v=1457542797037" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;"><br style="box-sizing: border-box;" />5. Chuối v&agrave;ng l&agrave; một dạng đột biến được ph&aacute;t hiện v&agrave;o năm 1836.<br style="box-sizing: border-box;" />Những quả chuối ban đầu thường kh&ocirc;ng c&oacute; vị ngọt v&agrave; thay v&igrave; m&agrave;u v&agrave;ng ,ch&uacute;ng c&oacute; m&agrave;u đỏ hoặc xanh l&aacute; c&acirc;y.<br style="box-sizing: border-box;" />ảnh chuối v&agrave;ng,sức khỏe,giảm căng thẳng,trượt vỏ chuối,quả chuối,tr&aacute;i c&acirc;y<br style="box-sizing: border-box;" />6. Chuối được trồng ở hơn 100 quốc gia v&agrave; c&oacute; hơn 300 loại chuối</p>', '30000', 25000, 'chuoi-tieu', '2016-04-25 04:17:59', 2, 1),
(7, 'DỨA ĐÀ LẠT', 'dua.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Dứa &ldquo;nổi tiếng&rdquo; l&agrave; nguồn cung cấp dồi d&agrave;o c&aacute;c vitamin v&agrave; t&aacute;c dụng chống l&atilde;o h&oacute;a tự nhi&ecirc;n cho phụ nữ.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Dứa tươi l&acirc;u hơn c&aacute;c quả kh&aacute;c</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Sau mua dứa về, bạn c&oacute; thể để dứa trong nhiệt độ ph&ograve;ng 1-2 ng&agrave;y trước khi ăn. Điều n&agrave;y kh&ocirc;ng những kh&ocirc;ng ảnh hưởng đến m&ugrave;i vị m&agrave; c&ograve;n l&agrave;m dứa mềm v&agrave; mọng nước hơn. Nếu bạn kh&ocirc;ng sẵn s&agrave;ng để ăn dứa sau hai ng&agrave;y, h&atilde;y cho dứa v&agrave;o trong hộp nhựa v&agrave; để trong ngăn m&aacute;t 3-5 ng&agrave;y nữa. Nếu bạn đ&atilde; bổ v&agrave; giữ n&oacute; trong hộp k&iacute;n tủ lạnh th&igrave; dứa sẽ vẫn tươi v&agrave; bổ dưỡng trong 6-9 ng&agrave;y.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">C&aacute;c tin li&ecirc;n quan<br style="box-sizing: border-box;" />C&ocirc;ng dụng thần k&igrave; của nước m&iacute;a<br style="box-sizing: border-box;" />6 c&ocirc;ng dụng thần k&igrave; từ quả chuối ch&iacute;n<br style="box-sizing: border-box;" />C&ocirc;ng dụng chữa bệnh tuyệt vời của rau khoai lang<br style="box-sizing: border-box;" />Những cấm kị kh&ocirc;ng thể bỏ qua khi ăn nho<br style="box-sizing: border-box;" />3 đối tượng cấm kị kh&ocirc;ng được ăn mướp đắng<br style="box-sizing: border-box;" />Dứa l&agrave; nguồn cung cấp dồi d&agrave;o lượng vitamin C</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Vitamin C trong dứa mang đến lợi &iacute;ch bất ngờ với l&agrave;n da v&igrave; đặc t&iacute;nh chống oxy h&oacute;a, đồng thời gi&uacute;p bảo vệ cơ thể bạn nhờ tăng sự đề kh&aacute;ng từ c&aacute;c gốc tự do. Nghi&ecirc;n cứu được c&ocirc;ng bố gần đ&acirc;y tr&ecirc;n website của Hội Da liễu New Zealand, DermNet NZ cho biết, c&aacute;c loại kem dưỡng da chứa th&agrave;nh phần vitamin C c&oacute; thể bảo vệ da chống lại những t&aacute;c động l&atilde;o h&oacute;a từ &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Bổ sung dứa thường xuy&ecirc;n v&agrave; c&aacute;c loại rau quả kh&aacute;c trong thực đơn h&agrave;ng ng&agrave;y giống như bạn d&ugrave;ng c&aacute;c loại kem chống nếp nhăn v&agrave; dưỡng da rẻ tiền nhưng mang đến hiệu quả tốt nhất.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Giải nhiệt, trẻ l&acirc;u</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Dứa kh&ocirc;ng chỉ d&ugrave;ng l&agrave;m m&oacute;n tr&aacute;ng miệng sau bữa ăn m&agrave; nước &eacute;p của tr&aacute;i dứa c&ograve;n gi&uacute;p giải nhiệt v&agrave; giải kh&aacute;t rất tốt. Nước cốt tr&aacute;i dứa gi&uacute;p giảm mệt mỏi v&igrave; n&oacute; c&oacute; chứa vitamin A, C, canxi, mangan&hellip; gi&uacute;p cơ thể tr&aacute;nh khỏi t&aacute;c hại của qu&aacute; tr&igrave;nh oxy h&oacute;a. V&igrave; vậy, uống nước &eacute;p từ tr&aacute;i dứa sẽ gi&uacute;p giảm stress v&agrave; l&agrave;m việc hiệu quả hơn. Những ly sinh tố m&aacute;t lạnh bổ dưỡng từ tr&aacute;i dứa sẽ l&agrave;m cho m&ugrave;a h&egrave; của bạn kh&ocirc;ng c&ograve;n n&oacute;ng nực m&agrave; trở n&ecirc;n ngọt ng&agrave;o, thơm m&aacute;t.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Ch&iacute;nh nhờ t&aacute;c dụng chống lại t&aacute;c hại của qu&aacute; tr&igrave;nh oxy h&oacute;a n&ecirc;n ăn dứa hoặc uống nước dứa &eacute;p đều đặn c&ograve;n đem đến cho bạn sự thanh xu&acirc;n. Một số thức uống đơn giản, dễ l&agrave;m từ dứa c&oacute; t&aacute;c dụng thanh nhiệt v&agrave; gi&uacute;p bạn trẻ l&acirc;u như: sinh tố dứa, sinh tố chuối dứa, sinh tố xo&agrave;i dứa, sinh tố dứa cam mật ong, sinh tố dứa sữa chua&hellip;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/dua-4.jpg?v=1457542519420" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;"><br style="box-sizing: border-box;" />C&aacute;ch l&agrave;m: Dứa gọt vỏ, bỏ hết mắt rồi rửa lại bằng nước lọc. Xắt dứa th&agrave;nh những miếng nhỏ để xay cho dễ v&agrave; nhanh nhuyễn.C&aacute;c loại quả kh&aacute;c cũng bỏ vỏ, bỏ hạt, xắt miếng nhỏ để dễ xay. Cho dứa (hoặc c&ugrave;ng quả kh&aacute;c), ch&uacute;t nước lọc, đường theo tỷ lệ vừa với khẩu vị của từng người v&agrave;o m&aacute;y xay sinh tố v&agrave; xay nhuyễn. Cho tiếp đ&aacute; b&agrave;o v&agrave;o hỗn hợp nước dứa đ&atilde; xay nhuyễn (nhiều hay &iacute;t đ&aacute; t&ugrave;y &yacute; th&iacute;ch của mỗi người) v&agrave; xay lại 1 l&uacute;c cho sinh tố m&aacute;t đều. Đổ sinh tố ra cốc, trang tr&iacute; cho đẹp mắt v&agrave; thưởng thức.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">&nbsp;Cải thiện hệ ti&ecirc;u h&oacute;a</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Bromelain l&agrave; một chất chiết xuất được t&igrave;m thấy trong th&acirc;n dứa, c&oacute; t&aacute;c dụng trung h&ograve;a dịch cơ thể để kh&ocirc;ng trở n&ecirc;n qu&aacute; axit. Bromelain cũng điều chỉnh hoạt động của tuyến tụy, hỗ trợ ti&ecirc;u h&oacute;a. Bạn c&oacute; thể gi&uacute;p cho đường ti&ecirc;u h&oacute;a khỏe mạnh v&igrave; dứa gi&uacute;p ti&ecirc;u h&oacute;a protein nhanh hơn.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Giữ nướu răng khỏe mạnh</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Bạn đ&aacute;nh răng nhiều lần trong ng&agrave;y, nhưng bạn c&oacute; ch&uacute; &yacute; đến nướu răng của m&igrave;nh? V&igrave; c&oacute; h&agrave;m lượng vitamin C cao, n&ecirc;n ăn dứa gi&uacute;p l&agrave;m giảm nguy cơ vi&ecirc;m nướu v&agrave; bệnh nha chu. Bệnh nha chu kh&ocirc;ng chỉ ph&aacute; hủy c&aacute;c m&ocirc; v&agrave; xương h&agrave;m m&agrave; c&ograve;n li&ecirc;n quan đến bệnh tim, đột quỵ v&agrave; tiểu đường. Vitamin C trong dứa gi&uacute;p cải thiện khả năng của cơ thể để chống lại vi khuẩn x&acirc;m nhập, g&oacute;p phần ph&ograve;ng chống vi&ecirc;m nướu v&agrave; bệnh nha chu.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">L&agrave;m giảm vi&ecirc;m khớp</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Dứa c&oacute; đặc t&iacute;nh chống vi&ecirc;m, do đ&oacute; th&ecirc;m dứa v&agrave;o chế độ ăn uống của bạn c&oacute; thể l&agrave;m giảm đau vi&ecirc;m khớp, cũng như bệnh g&uacute;t v&agrave; hội chứng ống cổ tay. Dứa cũng c&oacute; thể gi&uacute;p cải thiện tổng trạng bằng c&aacute;ch l&agrave;m xương chắc khỏe.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Ngăn ngừa cao huyết &aacute;p</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Nếu bạn đang cố gắng để giảm bớt huyết &aacute;p cao của m&igrave;nh, hoặc muốn tr&aacute;nh căn bệnh n&agrave;y, h&atilde;y ăn nhiều dứa. V&igrave; dứa c&oacute; lượng kali cao v&agrave; natri thấp, gi&uacute;p cơ thể duy tr&igrave; mức huyết &aacute;p b&igrave;nh thường.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Đặc t&iacute;nh chống ung thư</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">C&oacute; thể kh&ocirc;ng c&oacute; c&aacute;ch chữa bệnh ung thư, nhưng c&oacute; những điều gi&uacute;p bạn ngăn chặn n&oacute;. Dứa rất đầy đủ c&aacute;c chất chống oxy h&oacute;a, gi&uacute;p bạn chiến đấu chống lại c&aacute;c gốc tự do. C&aacute;c gốc tự do l&agrave; nh&oacute;m c&aacute;c nguy&ecirc;n tử g&acirc;y tổn thương khi tiếp x&uacute;c với m&agrave;ng tế b&agrave;o/ADN của bạn. Chất chống oxy h&oacute;a ngăn chặn c&aacute;c gốc tự do l&agrave;m tổn thương cơ thể của bạn, bằng c&aacute;ch giữ cho c&aacute;c tế b&agrave;o khỏe mạnh.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Ngăn ngừa ho v&agrave; cảm lạnh</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Dứa rất gi&agrave;u vitamin C, c&oacute; nghĩa l&agrave; dứa gi&uacute;p tăng cường hệ miễn dịch của bạn. Điều n&agrave;y gi&uacute;p bạn chống lại ho v&agrave; cảm lạnh. Thậm ch&iacute; nếu đ&atilde; bị bệnh, bạn n&ecirc;n ăn dứa v&igrave; dứa c&oacute; chứa bromelain, gi&uacute;p hạn chế chất nhầy v&agrave; ức chế ho.</p>', '27000', 25000, 'dua-da-lat', '2016-04-25 04:18:54', 2, 1),
(8, 'CÀ CHUA BI', 'cachuabi.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">M&oacute;n c&aacute; nấu n&oacute;ng hổi ng&agrave;y đ&ocirc;ng, m&oacute;n sườn sốt chua ngọt đưa cơm, m&oacute;n đậu sốt vừa ngon vừa l&agrave;nh&hellip; những m&oacute;n ăn hằng ng&agrave;y đ&oacute; v&agrave; rất nhiều m&oacute;n ngon kh&aacute;c nữa kh&ocirc;ng thể thiếu được sự g&oacute;p phần của quả c&agrave; chua. C&agrave; chua c&oacute; m&agrave;u đỏ tươi, đẹp mắt, khiến cho c&aacute;c m&oacute;n ăn trở n&ecirc;n hấp dẫn hơn, tạo cảm gi&aacute;c ngon miệng hơn. Tuy nhi&ecirc;n c&oacute; một điều bất ngờ l&agrave; kh&ocirc;ng những tạo cảm gi&aacute;c ngon miệng, đẹp mắt, tr&aacute;i c&agrave; chua c&ograve;n rất gi&agrave;u chất dinh dưỡng, tốt cho sức khỏe.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Theo ph&acirc;n t&iacute;ch khoa học th&igrave; trong 100g c&agrave; chua c&oacute; 94g nước, 0,6g protit, 4,2g gluxit, 0,8g xenlul&ocirc;, 0,4g tro, 12mg canxi, 26mg photpho, 1,4mg sắt, c&aacute;c loại vitamin caroten, vitamin B1, B2. PP, C... cung cấp được 20kcal.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;"><br style="box-sizing: border-box;" />Cung cấp Vitamin A v&agrave; nhiều chất dinh dưỡng cần thiết:<br style="box-sizing: border-box;" />M&agrave;u đỏ tươi trong mỗi tr&aacute;i c&agrave; chua thể hiện h&agrave;m lượng vitamin A thi&ecirc;n nhi&ecirc;n cao, trung b&igrave;nh chỉ cần 100g c&agrave; chua ch&iacute;n c&ograve;n tươi sẽ đ&aacute;p ứng được 13% nhu cầu hằng ng&agrave;y về vitamin A, cũng như c&aacute;c vitamin B6, vitamin C. Ngo&agrave;i ra c&ograve;n c&oacute; c&aacute;c vitamin B1, B2, PP...<br style="box-sizing: border-box;" />C&aacute;c chất kho&aacute;ng vi lượng c&oacute; trong c&agrave; chua &nbsp;như canxi, sắt, kali, photpho, magnesium, lưu huỳnh, nickel, cobalt, i&ocirc;t, c&aacute;c axit hữu cơ dưới dạng muối citrat v&agrave; tuỳ theo m&ocirc;i trường trồng, trong c&agrave; chua c&oacute; thể c&oacute; cả đồng, molibden. Ch&iacute;nh nhờ c&aacute;c yếu tố n&agrave;y, c&agrave; chua được coi l&agrave; một thức ăn gi&agrave;u chất dinh dưỡng, dễ ti&ecirc;u ho&aacute;, tăng cường sức đề kh&aacute;ng của cơ thể.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Chống l&atilde;o h&oacute;a, ngừa ung thư:<br style="box-sizing: border-box;" />Sắc tố lycopen trong c&agrave; chua hiện đang được đ&aacute;nh gi&aacute; c&ugrave;ng với b&ecirc;ta - caroten l&agrave; những chất chống oxy ho&aacute; mạnh, vừa ngăn chặn tế b&agrave;o ung thư, vừa chống sự h&igrave;nh th&agrave;nh c&aacute;c cục m&aacute;u đ&ocirc;ng trong th&agrave;nh mạch.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">C&ugrave;ng với tuổi t&aacute;c, c&aacute;c gốc tự do nội sinh v&agrave; ngoại sinh trong cơ thể sẽ ph&aacute; huỷ c&aacute;c DNA v&agrave; RNA l&agrave; những phần tử di truyền của tế b&agrave;o, tạo n&ecirc;n đột biến gen g&acirc;y ung thư, đồng thời ph&aacute; huỷ c&aacute;c tế b&agrave;o, l&agrave;m suy yếu c&aacute;c cơ quan dẫn đến bệnh tật v&agrave; gi&agrave; nua. Vitamin A, nhất l&agrave; vitamin A dưới dạng thi&ecirc;n nhi&ecirc;n b&ecirc;ta-caroten c&oacute; t&aacute;c dụng t&iacute;ch cực trong ph&ograve;ng chống hiện tượng n&agrave;y.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Qua thống k&ecirc; nghi&ecirc;n cứu, người ta thấy nguy cơ vỡ mạch m&aacute;u n&atilde;o ở những người ăn nhiều rau quả chứa vitamin A thi&ecirc;n nhi&ecirc;n thấp hơn hẳn những người &iacute;t ăn những thực phẩm n&agrave;y. Đ&oacute; l&agrave; nhờ vitamin A gi&uacute;p ngăn ngừa t&iacute;ch luỹ cholesterol tr&ecirc;n th&agrave;nh mạch n&ecirc;n tr&aacute;nh được nguy cơ vỡ mạch m&aacute;u n&atilde;o. Như vậy, c&agrave; chua với b&ecirc;ta - caroten v&agrave; lycopen sẽ g&oacute;p phần l&agrave;m chậm sự l&atilde;o ho&aacute; v&agrave; ph&ograve;ng ngừa ung thư.<br style="box-sizing: border-box;" />&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/cachuabi-3.jpg?v=1457542993053" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;"><br style="box-sizing: border-box;" />Ăn c&agrave; chua c&ograve;n xanh c&oacute; thể bị ngộ độc:<br style="box-sizing: border-box;" />Một lưu &yacute; đặc biệt khi sử dụng c&agrave; chua đ&oacute; l&agrave; c&agrave; chua c&ograve;n xanh c&oacute; chứa một loại ancaloid độc t&ecirc;n l&agrave; solanin, nhưng khi c&agrave; chua ch&iacute;n, chất độc n&agrave;y kh&ocirc;ng c&ograve;n nữa. V&igrave; vậy, tuyệt đối kh&ocirc;ng ăn c&agrave; chua c&ograve;n xanh, c&oacute; thể bị ngộ độc nguy hiểm. Nếu c&oacute; điều kiện, ăn c&agrave; chua ch&iacute;n c&acirc;y tốt hơn l&agrave; c&agrave; chua h&aacute;i xanh rồi ủ ch&iacute;n nh&acirc;n tạo.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Kh&ocirc;ng n&ecirc;n ăn c&agrave; chua sống. Nếu cần ăn, phải rửa c&agrave; chua thật kỹ nhiều lần như rửa rau sống, rồi d&ugrave;ng nước s&ocirc;i rửa sạch lần cuối cho an to&agrave;n trước khi ăn. Người bị sỏi mật tuyệt đối kh&ocirc;ng ăn c&agrave; chua sống v&igrave; lượng axit hữu cơ c&oacute; trong c&agrave; chua sống tương đối lớn, c&oacute; thể g&acirc;y co thắt t&uacute;i mật.</p>', '35000', 30000, 'ca-chua-bi', '2016-04-25 05:14:28', 2, 1),
(9, 'BƠ VỎ SẦN', 'bo.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Qủa bơ c&oacute; chứa hơn 14 loại vitamin v&agrave; kho&aacute;ng chất bao gồm canxi, sắt, đồng, magi&ecirc;, phốtpho, kali, natri, kẽm mangan v&agrave; selen. &nbsp;Đ&acirc;y l&agrave; loại tr&aacute;i c&acirc;y được mọi người ưa th&iacute;ch v&igrave; c&oacute; nhiều t&aacute;c dụng tốt đối với sức khỏe.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">T&aacute;c dụng của quả bơ đối với sức khỏe&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Ch&uacute;ng ta c&ugrave;ng t&igrave;m hiểu những t&aacute;c dụng của quả bơ đối với sức khỏe nh&eacute;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Giảm cholesterol<br style="box-sizing: border-box;" />Bơ c&oacute; chứa rất nhiều beta-sitosterol, l&agrave; một hợp chất l&agrave;m giảm tỉ lệ cholesterol. Một nghi&ecirc;n cứu tiến h&agrave;nh tr&ecirc;n 45 người đ&atilde; cho thấy ăn một quả mỗi ng&agrave;y sẽ gi&uacute;p giảm tỉ lệ chất b&eacute;o xuống khoảng 17% chỉ trong v&ograve;ng một tuần.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">T&aacute;c dụng của quả bơ đối với sức khỏe&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Bảo vệ tim mạch<br style="box-sizing: border-box;" />H&agrave;m lượng Folate c&oacute; trong quả bơ cũng tăng cường c&aacute;c hoạt động chức năng của thần kinh, nhờ t&aacute;c động k&eacute;p n&ecirc;n cho t&aacute;c dụng bảo vệ tim mạch tốt. Folate l&agrave;m giảm h&agrave;m lượng chất homocystein (thủ phạm ch&iacute;nh g&acirc;y ra c&aacute;c bệnh về tim mạch), c&ograve;n chất oleic acid l&agrave;m giảm chứng xơ vữa động mạch.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">T&aacute;c dụng của quả bơ đối với sức khỏe&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Trẻ h&oacute;a da, chống l&atilde;o h&oacute;a.<br style="box-sizing: border-box;" />Một trong t&aacute;c dụng của quả bơ đối với sức khỏe l&agrave; gi&uacute;p con người chống lại bệnh tật v&agrave; l&atilde;o h&oacute;a, bởi tinh dầu của bơ c&oacute; khả năng v&ocirc; hiệu h&oacute;a những ph&acirc;n tử g&acirc;y hại cho cơ thể đồng thời trong th&agrave;nh phần quả bơ chứa chất dầu c&oacute; t&aacute;c dụng l&agrave;m chậm qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a, chăm s&oacute;c da kh&ocirc; hiệu quả đặc biệt với phụ nữ.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/bo-4.jpg?v=1457618883520" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Chống ung thư<br style="box-sizing: border-box;" />T&aacute;c dụng của quả bơ đối với sức khỏe&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Một số hợp chất c&oacute; trong tr&aacute;i bơ c&oacute; thể ph&aacute;t hiện những tế b&agrave;o c&oacute; khả năng ung thư hoặc g&acirc;y ung thư miệng v&agrave; ti&ecirc;u diệt ch&uacute;ng m&agrave; kh&ocirc;ng g&acirc;y hại đến những tế b&agrave;o khỏe mạnh. Bơ c&oacute; chứa lượng axit oleic kh&aacute; cao. Đ&acirc;y l&agrave; loại axit gi&uacute;p ngăn ngừa ung thư v&uacute;, ung thư miệng v&agrave; ung thư thận.</p>', '45000', 40000, 'bo-vo-san', '2016-04-25 05:15:09', 2, 1),
(10, 'DƯA HẤU', 'duahau.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Dưa hấu l&agrave; một trong những loại hoa quả m&ugrave;a h&egrave; ngon v&agrave; được y&ecirc;u th&iacute;ch nhất. Nhưng c&oacute; những điều bạn chưa hề biết.<br style="box-sizing: border-box;" />8 sự thật k&igrave; diệu về dưa hấu 1 &nbsp;&nbsp;<br style="box-sizing: border-box;" />Dưa hấu chứa &iacute;t calo (46kg calo mỗi cốc), chứa h&agrave;m lượng vitamin A v&agrave; vitamin C cao (dưa hấu cung cấp 17% lượng vitamin A v&agrave; 20% lượng vitamin C cần thiết cho cơ thể bạn mỗi ng&agrave;y). Dưa hấu cũng rất gi&agrave;u chất xơ v&agrave; kali. Nhiều người tranh luận mạnh mẽ xem dưa hấu l&agrave; một loại rau hay tr&aacute;i c&acirc;y.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Nếu bạn muốn t&igrave;m hiểu một v&agrave;i sự thực đ&aacute;ng ngạc nhi&ecirc;n th&uacute; vị về dưa hấu, h&atilde;y ghi nhớ những điều sau.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Dưa hấu l&agrave;m tăng sức mạnh của n&atilde;o</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">8 sự thật k&igrave; diệu về dưa hấu 2&nbsp;<br style="box-sizing: border-box;" />Bạn c&oacute; biết rằng dưa hấu c&oacute; thể gi&uacute;p bạn tăng cường sức mạnh n&atilde;o bộ của bạn? Sự thực l&agrave;, dưa hấu l&agrave; một nguồn vitamin B6 tuyệt vời, chất dinh dưỡng quan trọng gi&uacute;p n&atilde;o hoạt động b&igrave;nh thường. Hơn nữa, tỷ lệ nước trong loại quả ngon l&agrave;nh n&agrave;y cũng tương tự như tỷ lệ nước trong n&atilde;o.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/duahau-2.jpg?v=1457851385390" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Dưa hấu c&oacute; h&agrave;m lượng nước cao</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">8 sự thật k&igrave; diệu về dưa hấu 3 &nbsp;&nbsp;<br style="box-sizing: border-box;" />Dưa hấu c&oacute; h&agrave;m lượng nước rất cao. Theo Sở N&ocirc;ng nghiệp Hoa Kỳ USDA, dưa hấu chứa 91,5 % nước, c&oacute; nghĩa l&agrave; ăn loại quả n&agrave;y c&oacute; thể gi&uacute;p bạn kh&ocirc;ng cẩn uống nước. Ăn dưa hấu hoặc uống nước &eacute;p dưa hấu mỗi ng&agrave;y l&agrave; một trong những c&aacute;ch &ldquo;ngon&rdquo; nhất để bổ sung nước trong m&ugrave;a n&agrave;y, phải kh&ocirc;ng n&agrave;o?</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Dưa hấu chứa lycopene nhiều hơn khoảng 40 phần trăm so với c&agrave; chua sống</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">8 sự thật k&igrave; diệu về dưa hấu 4 &nbsp;&nbsp;<br style="box-sizing: border-box;" />Một cốc dưa hấu c&oacute; chứa lycopene nhiều hơn 1,5 lần so với c&agrave; chua tươi. Lycopene l&agrave; một chất chống oxy h&oacute;a gi&uacute;p ngăn ngừa c&aacute;c gốc tự do g&acirc;y hư hại tế b&agrave;o v&agrave; c&oacute; thể cải thiện hệ thống miễn dịch của bạn. Hơn nữa, chất chống oxy h&oacute;a mạnh mẽ n&agrave;y đ&atilde; được chứng minh l&agrave; giảm nguy cơ ung thư trong cơ thể.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Dưa hấu tốt cho đ&ocirc;i mắt của bạn</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">8 sự thật k&igrave; diệu về dưa hấu 5 &nbsp;&nbsp;<br style="box-sizing: border-box;" />Dưa hấu chưa nhiều Vitamin A rất tốt cho mắt của bạn. N&oacute; gi&uacute;p tăng cường sức mạnh thị lực của bạn v&agrave; bảo vệ đ&ocirc;i mắt của bạn khỏi bị nhiễm tr&ugrave;ng. Ti&ecirc;u thụ khoảng 100 gram dưa hấu mỗi ng&agrave;y để giữ cho đ&ocirc;i mắt của bạn khỏe mạnh v&agrave; thị lực của bạn sắc n&eacute;t.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Nước &eacute;p dưa hấu c&oacute; thể l&agrave;m giảm đau nhức cơ bắp</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">8 sự thật k&igrave; diệu về dưa hấu 6 &nbsp;&nbsp;<br style="box-sizing: border-box;" />Nhiều nghi&ecirc;n cứu cho thấy nước &eacute;p dưa hấu c&oacute; thể gi&uacute;p giảm đau nhức cơ bắp, v&igrave; vậy n&oacute; l&agrave; một thức uống tuyệt vời sau khi tập luyện v&ocirc; c&ugrave;ng mệt mỏi. C&oacute; điều l&agrave;, dưa hấu l&agrave; một trong những loại thực phẩm chứa nhiều citrulline nhất, gi&uacute;p cải thiện chức năng của động mạch v&agrave; giảm huyết &aacute;p. Nếu bạn bị đau nhức cơ bắp, h&atilde;y uống nước &eacute;p. Trước đ&oacute;, bạn n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Dưa hấu gi&uacute;p giảm c&acirc;n</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">8 sự thật k&igrave; diệu về dưa hấu 7 &nbsp;&nbsp;<br style="box-sizing: border-box;" />Ăn dưa hấu l&agrave; một c&aacute;ch ngon v&agrave; tiết kiệm để l&agrave;m sạch cơ thể v&agrave; giảm c&acirc;n. Dưa hấu chứa &iacute;t calo v&agrave; h&agrave;m lượng nước cao v&agrave; điều n&agrave;y sẽ gi&uacute;p bạn giảm c&acirc;n. Dưa hấu c&oacute; thể l&agrave;m nước &eacute;p v&agrave; m&oacute;n ăn nhẹ giữa c&aacute;c bữa v&igrave; n&oacute; cung cấp cho cơ thể nhiều kho&aacute;ng chất v&agrave; nước.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Dưa hấu bảo vệ bạn khỏi tia UV</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">8 sự thật k&igrave; diệu về dưa hấu 8 &nbsp;&nbsp;<br style="box-sizing: border-box;" />Dưa hấu c&oacute; nhiều chất lycopene c&oacute; t&iacute;nh chống oxy h&oacute;a mạnh mẽ. Lycopene gi&uacute;p bảo vệ bạn chống lại tia UV, ch&aacute;y nắng v&agrave; ung thư da. Đ&oacute; l&agrave; một l&yacute; do quan trọng để kết hợp một số dưa hấu v&agrave;o chế độ ăn uống h&agrave;ng ng&agrave;y của bạn.</p>', '45000', 35000, 'dua-hau', '2016-04-25 05:16:26', 2, 1),
(11, 'SÚP LƠ XANH', 'suplo.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Đối với cơ thể con người th&igrave; th&agrave;nh phần kho&aacute;ng chất-vitamin của s&uacute;p lơ rất c&oacute; &iacute;ch bởi n&oacute; c&oacute; t&aacute;c dụng từ nhiều mặt: K&iacute;ch th&iacute;ch c&aacute;c qu&aacute; tr&igrave;nh trao đổi chất v&agrave; tham gia v&agrave;o hoạt động tạo m&aacute;u để củng cố hệ tim-mạch, cũng như c&aacute;c chức năng bảo vệ cơ thể. V&igrave; thế m&agrave; s&uacute;p lơ kh&ocirc;ng chỉ được coi l&agrave; một m&oacute;n ăn dinh dưỡng qu&yacute; gi&aacute; m&agrave; c&ograve;n l&agrave; một th&agrave;nh phần dinh dưỡng quan trọng đối với trẻ em.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/suplo-2.jpg?v=1457535858247" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Ưu thế trong s&uacute;p lơ l&agrave; đường fructose v&agrave; glucose, l&agrave;m cho n&oacute; trở th&agrave;nh loại rau c&oacute; th&agrave;nh phần dinh dưỡng tốt đối với những bệnh nh&acirc;n tiểu đường. C&ograve;n đối với những người c&oacute; bệnh về hệ tim-mạch th&igrave; s&uacute;p lơ cũng c&oacute; &iacute;ch bởi n&oacute; gi&uacute;p đ&agrave;o thải những cholesterin &ldquo;xấu&rdquo; v&agrave; củng cố mạch m&aacute;u.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">H&agrave;m lượng axit tartric c&oacute; trong s&uacute;p lơ gi&uacute;p ph&ograve;ng tr&aacute;nh chứng b&eacute;o ph&igrave;. S&uacute;p lơ, cũng như bắp cải, s&uacute;p lơ xanh được coi l&agrave; thực đơn tốt c&oacute; đặc t&iacute;nh ph&ograve;ng ngừa c&aacute;c bệnh ung thư. C&oacute; thể sử dụng s&uacute;p lơ để điều trị chứng t&aacute;o b&oacute;n, c&aacute;c bệnh về gan v&agrave; chứng ch&aacute;n ăn. Khi bị vi&ecirc;m lợi c&oacute; thể s&uacute;c miệng bằng nước &eacute;p s&uacute;p lơ tươi h&ograve;a với nước đun s&ocirc;i để ấm theo tỷ lệ 1:1.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/suplo-4.jpg?v=1457535858247" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Hợp chất bột nh&atilde;o từ l&aacute; s&uacute;p lơ h&ograve;a với l&ograve;ng trắng để điều trị vết bỏng v&agrave; c&aacute;c vết thương c&oacute; sẹo. C&aacute;ch l&agrave;m l&agrave; nghiền n&aacute;t l&aacute;, th&ecirc;m l&ograve;ng đỏ trứng g&agrave; v&agrave; đặt l&ecirc;n v&agrave;i lớp gạc, sau đ&oacute; đắp l&ecirc;n chỗ vết thương v&agrave; băng lại.</p>', '27000', 25000, 'sup-lo-xanh', '2016-04-25 05:17:22', 3, 1),
(12, 'RAU THƠM', 'raumuithom.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">T&iacute;a t&ocirc;, diếp c&aacute;, m&ugrave;i t&agrave;u, h&uacute;ng quế, sả... kh&ocirc;ng chỉ l&agrave; những loại rau quen thuộc, mang lại hương vị ri&ecirc;ng trong mỗi bữa ăn của c&aacute;c gia đ&igrave;nh m&agrave; c&ograve;n l&agrave; vị thuốc đặc biệt. Dưới đ&acirc;y l&agrave; c&ocirc;ng dụng chữa bệnh kỳ diệu của một số loại rau thơm.&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/raumuithom-2.jpg?v=1457536152060" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">H&uacute;ng quế&nbsp;<br style="box-sizing: border-box;" />&nbsp;&nbsp;<br style="box-sizing: border-box;" />Theo Đ&ocirc;ng y, h&uacute;ng quế c&oacute; vị cay, t&iacute;nh n&oacute;ng, c&oacute; m&ugrave;i thơm, c&oacute; t&aacute;c dụng l&agrave;m ra mồ h&ocirc;i, lợi tiểu, giảm đau. Quả c&oacute; vị ngọt v&agrave; cay, t&iacute;nh m&aacute;t, c&oacute; t&aacute;c dụng tốt cho thị lực. To&agrave;n c&acirc;y c&oacute; t&aacute;c dụng chữa cảm c&uacute;m, cảm sốt nhức đầu, nghẹt mũi, đầy bụng, k&eacute;m ti&ecirc;u.&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">H&uacute;ng bạc h&agrave;&nbsp;<br style="box-sizing: border-box;" />&nbsp;&nbsp;<br style="box-sizing: border-box;" />Bạc h&agrave; l&agrave; một b&agrave;i thuốc kh&aacute; hữu hiệu trong việc chữa trị cảm c&uacute;m v&agrave; c&aacute;c vết c&ocirc;n tr&ugrave;ng cắn, gi&uacute;p lọi tểu h&oacute;a, chữa chứng đầy hơi, thấp khớp, nấc cục, th&ocirc;ng cổ, trị vi&ecirc;m xoang nhẹ&hellip; Dầu bạc h&agrave; cay c&oacute; thể l&agrave;m dịu cơn hen suyễn nhẹ bằng c&aacute;ch ngửi. Phụ nữ c&oacute; mang th&igrave; n&ecirc;n thận trọng khi d&ugrave;ng v&igrave; rất c&oacute; thể dẫn đến sẩy thai.&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><br style="box-sizing: border-box;" />&nbsp;&nbsp;<img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/raumuithom-4.jpg?v=1457536152060" /><br style="box-sizing: border-box;" />T&iacute;a t&ocirc;&nbsp;<br style="box-sizing: border-box;" />&nbsp;&nbsp;<br style="box-sizing: border-box;" />Kh&ocirc;ng chỉ l&agrave; rau gia vị thơm ngon, t&iacute;a t&ocirc; c&ograve;n l&agrave; c&acirc;y thuốc được d&ugrave;ng phổ biến trong y học cổ truyền. T&iacute;a t&ocirc; c&oacute; hai loại: t&iacute;a t&ocirc; m&eacute;p l&aacute; phẳng, m&agrave;u t&iacute;a nhạt, &iacute;t thơm v&agrave; t&iacute;a t&ocirc; m&eacute;p l&aacute; quăn, m&agrave;u t&iacute;a sẫm, m&ugrave;i thơm nồng.<br style="box-sizing: border-box;" />T&iacute;a t&ocirc; c&oacute; vị cay, t&iacute;nh ấm, c&oacute; t&aacute;c dụng trị cảm lạnh, đầy bụng, n&ocirc;n mửa. C&agrave;nh t&iacute;a t&ocirc; c&oacute; vị cay ngọt, c&oacute; t&aacute;c dụng an thai, chống n&ocirc;n mửa, giảm đau, hen suyễn. Ngo&agrave;i ra, với gi&aacute; trị dinh dưỡng cao, gi&agrave;u vitamin A, C, gi&agrave;u h&agrave;m lượng Ca, Fe, v&agrave; P... t&iacute;a t&ocirc; c&oacute; t&aacute;c dụng đẹp da. Ch&iacute;nh v&igrave; thế, nhiều người đ&atilde; d&ugrave;ng t&iacute;a t&ocirc; như một phương thuốc l&agrave;m đẹp da &iacute;t tốn k&eacute;m, nhưng hiệu quả</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Rau diếp c&aacute;&nbsp;<br style="box-sizing: border-box;" />&nbsp;&nbsp;<br style="box-sizing: border-box;" />Theo thạc sĩ, Dược sĩ Đo&agrave;n Xu&acirc;n Đinh, Khoa Dược, viện Y học cổ truyền Qu&acirc;n đội cũng cho biết, m&ugrave;i tanh tanh của rau diếp c&aacute; khi v&ograve; ra ch&iacute;nh l&agrave; tinh của n&oacute;. Tinh dầu n&agrave;y ch&iacute;nh l&agrave; c&ocirc;ng dụng qu&yacute; của diếp c&aacute;. L&aacute; v&agrave; phần th&acirc;n b&ograve; tr&ecirc;n mặt đất của n&oacute; c&oacute; những chất kết hợp c&ugrave;ng với tinh dầu mang t&aacute;c dụng điều trị bệnh. Với gi&aacute; trị l&agrave;m thuốc thanh nhiệt, giải độc do l&agrave;m m&aacute;t huyết trong cơ thể n&ecirc;n trong c&aacute;c trường hợp bị vi&ecirc;m nhiễm trong cơ thể như mụn nhọt, mẩn ngứa c&oacute; thể d&ugrave;ng rau diếp c&aacute; l&agrave;m m&aacute;t m&aacute;u. Hoặc khi bị bệnh đường ruột, bị ti&ecirc;u chảy cũng c&oacute; thể d&ugrave;ng diếp c&aacute;.&nbsp;<br style="box-sizing: border-box;" />&nbsp; &nbsp;<br style="box-sizing: border-box;" />Rau diếp c&aacute; c&oacute; thể d&ugrave;ng để hạ sốt cho trường hợp trẻ sốt m&agrave; kh&ocirc;ng muốn d&ugrave;ng thuốc T&acirc;y, hoặc phụ nữ c&oacute; thai kh&ocirc;ng d&ugrave;ng được T&acirc;n dược. Rau diếp c&aacute; c&ograve;n c&oacute; thể d&ugrave;ng để trị t&aacute;o b&oacute;n do đại tr&agrave;ng bị nhiệt, hay d&ugrave;ng chữa mụn nhọt, lở ngứa.&nbsp;<br style="box-sizing: border-box;" />&nbsp;&nbsp;<br style="box-sizing: border-box;" />Sả &nbsp;<br style="box-sizing: border-box;" />&nbsp;&nbsp;<br style="box-sizing: border-box;" />Sả rất tốt cho hệ ti&ecirc;u h&oacute;a, c&oacute; t&aacute;c dụng k&iacute;ch th&iacute;ch ra mồ h&ocirc;i, hạ sốt ở người bệnh cảm v&agrave; gi&uacute;p lợi tiểu. Sả c&ograve;n được d&ugrave;ng để điều trị chứng co thắt cơ, chuột r&uacute;t, thấp khớp, đau đầu..&nbsp;<br style="box-sizing: border-box;" />&nbsp;&nbsp;<br style="box-sizing: border-box;" />L&aacute; lốt&nbsp;<br style="box-sizing: border-box;" />&nbsp;&nbsp;<br style="box-sizing: border-box;" />L&aacute; lốt thuộc họ hồ ti&ecirc;u ( Piperaceae), L&aacute; lốt l&agrave; loại c&acirc;y mọc hoang v&agrave; được trồng ở khắp mọi nơi. L&aacute; lốt c&oacute; c&ocirc;ng dụng ấm trung ti&ecirc;u, ấm dạ d&agrave;y. Chữa n&ocirc;n mửa do bị kh&iacute; lạnh bụng đầy đau. Chữa nhức đầu, đau răng, mũi lu&ocirc;n lu&ocirc;n chảy nước, đại tiện lỏng ra nước, ra m&aacute;u. Trong nh&acirc;n d&acirc;n, l&aacute; lốt thường được d&ugrave;ng chữa c&aacute;c bệnh sau: Chữa đau nhức xương khớp, Chữa bệnh phụ khoa (c&aacute;c vi&ecirc;m nhiễm ở v&ugrave;ng &acirc;m đạo, ngứa, ra kh&iacute; hư, Chữa đổ mồ h&ocirc;i nhiều ở tay ch&acirc;n, Chữa bệnh tổ đỉa ở b&agrave;n tay, Chữa đau rang, Chữa vi&ecirc;m xoang, chảy nước mũi đặc, Giải say nắng, Chữa đau bụng lạnh, đi ti&ecirc;u ph&acirc;n lỏng, buồn n&ocirc;n, nấc cụt&hellip;&nbsp;</p>', '5000', 3000, 'rau-thom', '2016-04-25 05:18:10', 3, 1);
INSERT INTO `products` (`id`, `name`, `image`, `description`, `price`, `sale_price`, `slug`, `created`, `category_id`, `status`) VALUES
(13, 'RAU CẦN XÀO BÒ', 'raucan.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Cần t&acirc;y kh&ocirc;ng phải l&agrave; loại rau qu&aacute; xa lạ đối với mọi người, n&oacute; cho vị ngon ngọt khi kết hợp trong một số m&oacute;n ăn. Ngo&agrave;i ngon miệng, c&aacute;c chuy&ecirc;n gia dinh dưỡng&nbsp;c&ograve;n khuy&ecirc;n uống nước cần t&acirc;y l&agrave; v&ocirc; c&ugrave;ng l&agrave;nh mạnh</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/raucan-2.jpg?v=1457541363607" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Loại rau củ n&agrave;y c&oacute; nguồn gốc từ Trung Đ&ocirc;ng &agrave; Địa Trung Hải, tuy nhi&ecirc;n, cần t&acirc;y được ti&ecirc;u thụ hầu hết ở c&aacute;c nước.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Kh&ocirc;ng giống như nhiều loại rau kh&aacute;c, cần t&acirc;y c&oacute; thể được ti&ecirc;u thụ từ rễ xuống đến tận gốc. Ngo&agrave;i việc kết hợp với một số thực phẩm kh&aacute;c cho m&oacute;n x&agrave;o, lẩu, canh th&igrave; cần t&acirc;y c&ograve;n được d&ugrave;ng để &eacute;p nước uống. Nước &eacute;p cần t&acirc;y c&oacute; kh&aacute; nhiều c&ocirc;ng dụng đối với bệnh tiểu đường, cao huyết &aacute;p v&agrave; cả tim mạch.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Đặc biệt, cần t&acirc;y chứa h&agrave;m lượng vitamin C dồi d&agrave;o gi&uacute;p cơ thể tăng sức đề kh&aacute;ng để chống lại bệnh tật. Dưới đ&acirc;y l&agrave; một số l&yacute; do đủ để thuyết phục bạn th&ecirc;m cần t&acirc;y v&agrave;o bữa ăn h&agrave;ng ng&agrave;y:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">1. Hỗ trợ điều trị nhiễm tr&ugrave;ng b&agrave;ng quang</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Cần t&acirc;y l&agrave; thực phẩm c&oacute; t&aacute;c dụng lợi tiểu, chống vi&ecirc;m n&ecirc;n rất hữu &iacute;ch trong việc đ&agrave;o thải sỏi mật v&agrave; hỗ trợ điều trị bệnh vi&ecirc;m b&agrave;ng quang.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">2. Điều trị bệnh về xương khớp</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Nhiều nghi&ecirc;n cứu đ&atilde; ph&aacute;t hiện ra rằng cần t&acirc;y c&oacute; thể hỗ trợ điều trị vi&ecirc;m đau c&aacute;c khớp, bệnh g&uacute;t v&agrave; vi&ecirc;m khớp dạng thấp nhờ v&agrave;o t&iacute;nh chất chống vi&ecirc;m mạnh mẽ.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">&nbsp;H&atilde;y d&ugrave;ng một ly nước &eacute;p cần t&acirc;y mỗi ng&agrave;y để c&oacute; được kết quả tối ưu nhất.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Để điều trị bệnh n&agrave;y, h&atilde;y d&ugrave;ng một ly nước &eacute;p cần t&acirc;y mỗi ng&agrave;y để c&oacute; được kết quả tối ưu nhất.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">3. Giảm vi&ecirc;m</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Như đ&atilde; n&ecirc;u ở tr&ecirc;n, cần t&acirc;y chứa chất chống vi&ecirc;m mạnh mẽ n&ecirc;n c&oacute; khả năng gi&uacute;p giảm đau do vi&ecirc;m nhiễm.&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">4. Kiểm so&aacute;t huyết &aacute;p</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Cần t&acirc;y rất gi&agrave;u kali - một trong những chất hữu &iacute;ch trong việc điều chỉnh huyết &aacute;p đối với người mắc bệnh cao huyết &aacute;p. Hơn nữa, n&oacute; c&oacute; chứa phthalides gi&uacute;p thư gi&atilde;n c&aacute;c cơ bắp xung quanh c&aacute;c động mạch. Điều n&agrave;y gi&uacute;p c&aacute;c mạch m&aacute;u gi&atilde;n nở, huyết &aacute;p hạ th&ecirc;m.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/raucan-3.jpg?v=1457541363607" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">5. Ngăn ngừa bệnh ung thư</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">C&aacute;c hợp chất c&oacute; trong cần t&acirc;y hỗ trợ cơ thể trong cuộc chiến chống lại bệnh ung thư.&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">C&aacute;c hợp chất c&oacute; trong cần t&acirc;y hỗ trợ cơ thể trong cuộc chiến chống lại bệnh ung thư. C&aacute;c luteolin flavonoid c&oacute; t&aacute;c dụng cản trở sự ph&aacute;t triển của c&aacute;c tế b&agrave;o ung thư. Phthalides cũng như polyacetylenes trong c&acirc;y cần t&acirc;y c&oacute; t&aacute;c dụng giải độc c&aacute;c tế b&agrave;o ung thư v&agrave; gi&uacute;p ngăn ngừa căn bệnh n&agrave;y kh&aacute; hiệu quả.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">6. Hiệu quả đối với giảm c&acirc;n</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Cần t&acirc;y l&agrave; một th&agrave;nh phần tuyệt vời trong chế độ ăn ki&ecirc;ng của bạn, nhất l&agrave; đối với người mong muốn đốt ch&aacute;y phần mỡ thừa.&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Cụ thể, một th&acirc;n c&acirc;y cần t&acirc;y trung b&igrave;nh chỉ c&oacute; 15 đơn vị calo. Bạn c&oacute; thể ăn nhiều cần t&acirc;y m&agrave; kh&ocirc;ng bị lo ngại về lượng calo qu&aacute; cao, th&agrave;nh phần chất xơ trong cần t&acirc;y sẽ khiến bạn no l&acirc;u hơn.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">7. Giảm đau bụng kinh nguyệt</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Hiện c&oacute; 50 hợp chất kh&aacute;c trong cần t&acirc;y c&oacute; thể "đối ph&oacute;" với c&aacute;c triệu chứng đau tức của chu kỳ kinh nguyệt. C&aacute;c Athenol phytochemical trong cần t&acirc;y gi&uacute;p hạ c&aacute;c cơn n&oacute;ng trong cơ thể người phụ nữ l&uacute;c h&agrave;nh kinh.</p>', '7000', 5000, 'rau-can-xao-bo', '2016-04-25 05:20:04', 3, 1),
(14, 'MƯỚP HƯƠNG', 'muop.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Mướp l&agrave; một loại thực phẩm rất quen thuộc đối với người d&acirc;n Việt Nam, thời cổ xưa, mướp c&ograve;n được gọi l&agrave; "quả mỹ nh&acirc;n" bởi đ&oacute; l&agrave; thực phẩm l&agrave;m đẹp v&ocirc; c&ugrave;ng đ&aacute;ng qu&yacute;.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Xem th&ecirc;m c&ocirc;ng dụng của c&aacute;c loại rau<br style="box-sizing: border-box;" />C&ocirc;ng dụng chữa bệnh kh&ocirc;ng ngờ của rau mồng tơi<br style="box-sizing: border-box;" />T&aacute;c dụng chữa bệnh thần kỳ của rau m&aacute;<br style="box-sizing: border-box;" />12 l&yacute; do ch&iacute;nh đ&aacute;ng bạn n&ecirc;n ăn rau hẹ mỗi ng&agrave;y<br style="box-sizing: border-box;" />C&ocirc;ng dụng chữa bệnh thần kỳ của rau m&ugrave;i t&agrave;u<br style="box-sizing: border-box;" />C&ocirc;ng dụng chữa bệnh bất ngờ của rau cải c&uacute;c<br style="box-sizing: border-box;" />Ngo&agrave;i ra, mướp kh&ocirc;ng chỉ l&agrave; m&oacute;n ăn ngon m&agrave; c&ograve;n l&agrave; một vị thuốc chữa được nhiều bệnh. &nbsp;Điều đặc biệt l&agrave; tất cả c&aacute;c bộ phận của mướp đều được sử dụng m&agrave; kh&ocirc;ng c&oacute; bất kỳ t&aacute;c dụng phụ n&agrave;o.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/muop-2.jpg?v=1457541687300" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">M&ugrave;a mướp sắp bắt đầu rồi. H&atilde;y tăng cường đưa v&agrave;o thực đơn gia đ&igrave;nh m&igrave;nh loại quả n&agrave;y nh&eacute;.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Lợi sữa</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">D&ugrave;ng 1 quả mướp tươi, 10g muối ăn, nấu s&ocirc;i với 1 l&iacute;t nước, cho sản phụ uống đến khi sữa ra nhiều. C&oacute; thể nấu mướp với ch&acirc;n gi&ograve; lợn để ăn với cơm h&agrave;ng ng&agrave;y. Ăn liền 5 ng&agrave;y.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Giải nhiệt</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Mướp: 500 g rửa thật sạch, cắt nhỏ, &eacute;p lấy nước (d&ugrave;ng m&aacute;y &eacute;p l&agrave; tốt nhất) rồi h&ograve;a với đường trắng, d&ugrave;ng l&agrave;m nước giải kh&aacute;t trong ng&agrave;y. C&ocirc;ng dụng: thanh nhiệt h&oacute;a đ&agrave;m, ti&ecirc;u vi&ecirc;m, chỉ kh&aacute;t.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;"><br style="box-sizing: border-box;" />Chữa vi&ecirc;m họng</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">L&aacute; mướp hương rửa sạch, gi&atilde; nhỏ với &iacute;t muối, th&ecirc;m nước, gạn uống l&agrave;m một lần. Chữa ho, hen k&eacute;o d&agrave;i: L&aacute; mướp hương 15g nấu nước uống hoặc chế biến dưới dạng cao lỏng 1/1, mỗi lần uống 0,5ml.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Chữa sốt cao, đau đầu</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Hoa mướp 20g, hạt đậu xanh 100g. Đậu xanh để cả vỏ, ninh nhừ rồi lấy khoảng 400ml nước cốt. Vớt x&aacute;c đậu xanh ra, cho hoa mướp đ&atilde; th&aacute;i nhỏ v&agrave;o, đun s&ocirc;i trong 5 &ndash; 10 ph&uacute;t. Để nguội. Chắt lấy nước uống l&agrave;m 2 &ndash; 3 lần trong ng&agrave;y.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Điều h&ograve;a kinh nguyệt</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">D&ugrave;ng 1 quả mướp kh&ocirc; đốt tồn t&iacute;nh, t&aacute;n bột, uống ng&agrave;y 10g v&agrave;o l&uacute;c s&aacute;ng sớm đ&oacute;i bụng. Mỗi liệu tr&igrave;nh 10 ng&agrave;y.<br style="box-sizing: border-box;" />&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Mướp nấu canh t&ocirc;m l&agrave; m&oacute;n ngon tuyệt vời<br style="box-sizing: border-box;" />Mướp nấu canh t&ocirc;m l&agrave; m&oacute;n ngon tuyệt vời<br style="box-sizing: border-box;" />Điều trị vi&ecirc;m đường tiết niệu: Mướp 200g, rửa sạch, cho 350ml nước nấu nhừ cho &iacute;t mật ong m&agrave; ăn, ng&agrave;y ăn 2 lần. 10 ng&agrave;y một liệu tr&igrave;nh.<br style="box-sizing: border-box;" />&nbsp;<br style="box-sizing: border-box;" />Trị nổi mề đay</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">L&aacute; mướp tươi 1 nắm, nghiền n&aacute;t th&agrave;nh nước, th&ecirc;m v&agrave;o một ch&uacute;t băng phiến, b&ocirc;i l&ecirc;n vết lở, nổi.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Trị mồ h&ocirc;i ch&acirc;n qu&aacute; nhiều<br style="box-sizing: border-box;" />&nbsp;<br style="box-sizing: border-box;" />Lấy mướp gi&agrave; đốt th&agrave;nh tro, tản rắc ở trong gi&agrave;y, ch&acirc;n trần kh&ocirc;ng tất đi v&agrave;o gi&agrave;y li&ecirc;n tục 15 ng&agrave;y.</p>', '15000', 10000, 'muop-huong', '2016-04-25 05:35:38', 3, 1),
(15, 'BÍ ĐAO', 'bidao.jpg', '<div class="tab-content" style="box-sizing: border-box; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; line-height: 24.9998px;">\r\n<div id="tab-1" class="tab-pane active" style="box-sizing: border-box; padding: 15px 0px;">\r\n<div class="rte" style="box-sizing: border-box; text-align: justify;">\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">Chị em phụ nữ c&oacute; lẽ đ&atilde; qu&aacute; quen thuộc với nước &eacute;p b&iacute; đao l&agrave; phương thuốc hữu hiệu để giảm c&acirc;n. Kh&ocirc;ng chỉ vậy, nước &eacute;p b&iacute; đao c&ograve;n c&oacute; nhiều t&aacute;c dụng kh&ocirc;ng ngờ nữa đấy. H&atilde;y c&ugrave;ng t&igrave;m hiểu th&ecirc;m nhiều nhiều những c&ocirc;ng dụng tuyệt vời của nước &eacute;p b&iacute; đao nữa nh&eacute;.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/bd4.jpg?v=1459130689067" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">C&aacute;ch l&agrave;m mứt xo&agrave;i cho ng&agrave;y tết th&ecirc;m m&agrave;u sắc</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">C&aacute;ch ng&acirc;m quả mơ với đường đơn giản</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">C&aacute;ch pha nước chấm nem cực ngon v&agrave; đơn giản</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">nước &eacute;p b&iacute; đao c&ocirc;ng dụng 1</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">Giảm c&acirc;n:<br style="box-sizing: border-box;" />nước &eacute;p b&iacute; đao c&ocirc;ng dụng 2</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">C&oacute; thể nhắc đ&ecirc;n đầu ti&ecirc;n l&agrave; t&aacute;c dụng giảm c&acirc;n của nước &eacute;p b&iacute; đao. B&iacute; đao c&oacute; c&ocirc;ng dụng giảm c&acirc;n chủ yếu l&agrave; bởi v&igrave; b&iacute; đao c&oacute; khả năng l&agrave;m no bụng m&agrave; kh&ocirc;ng chứa năng lượng nhiều.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">B&iacute; đao l&agrave; loại quả c&oacute; khả năng sinh nhiệt thấp, h&agrave;m lượng chất b&eacute;o gần như kh&ocirc;ng c&oacute; v&agrave; c&oacute; khả năng l&agrave;m giảm t&iacute;ch tụ mỡ trong cơ thể n&ecirc;n n&oacute; l&agrave; vị thuốc l&yacute; tưởng để chữa bệnh b&eacute;o ph&igrave;, ăn b&iacute; đao l&acirc;u d&agrave;i c&oacute; thể gi&uacute;p cho bạn c&oacute; một cơ thể thanh tho&aacute;t. Mỗi ng&agrave;y, bạn n&ecirc;n uống từ 0,2 đến 0,5 l&iacute;t nước b&iacute; đao, sẽ c&oacute; t&aacute;c dụng giảm c&acirc;n nhanh ch&oacute;ng.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">l&agrave;m đẹp da:<br style="box-sizing: border-box;" />nước &eacute;p b&iacute; đao c&ocirc;ng dụng 3</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">B&ecirc;n cạnh t&aacute;c dụng chữa bệnh, việc d&ugrave;ng nước &eacute;p b&iacute; đao tươi thường xuy&ecirc;n sẽ gi&uacute;p l&agrave;n da trở n&ecirc;n tươi s&aacute;ng hơn một c&aacute;ch r&otilde; rệt do b&iacute; đao chứa rất nhiều nước v&agrave; vitamin tốt cho l&agrave;n da, l&agrave;m thay đổi độ ẩm của da, l&agrave;m mịn da, l&agrave;m mờ vết n&aacute;m v&agrave; vết th&acirc;m do mụn,&hellip;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">C&oacute; thể uống nước &eacute;p b&iacute; đao hoặc d&ugrave;ng nước &eacute;p b&ocirc;i l&ecirc;n da mặt hoặc trộn lẫn nước &eacute;p b&iacute; đao với nước &eacute;p dưa chuột hoặc mật ong b&ocirc;i l&ecirc;n da mặt, khoảng 30 ph&uacute;t sau rửa lại bằng nước sạch.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">Đặc biệt nếu như da bạn bị n&aacute;m, đen th&igrave; sau khi &eacute;p b&iacute; lấy nước, bạn n&ecirc;n để lại một ch&uacute;t nước &eacute;p rồi trộn với mật ong, đắp l&ecirc;n mặt, c&aacute;c vết n&aacute;m tr&ecirc;n mặt sẽ mờ dần đi.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">Giải nhiệt, giải độc, ph&ograve;ng chống say nắng, say n&oacute;ng:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">&nbsp;<br style="box-sizing: border-box;" />nước &eacute;p b&iacute; đao c&ocirc;ng dụng 4</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">+ Th&aacute;i dưa hấu v&agrave; b&iacute; đao th&agrave;nh miếng nhỏ, d&ugrave;ng m&aacute;y &eacute;p hoặc gi&atilde; nhỏ vắt lấy nước rồi, th&ecirc;m một ch&uacute;t đường trắng h&ograve;a tan, chia uống nhiều lần trong ng&agrave;y.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">+ Hoặc: B&iacute; đao để nguy&ecirc;n vỏ, cho l&ecirc;n bếp than nướng, trở tay li&ecirc;n tục cho tr&aacute;i b&iacute; v&agrave;ng đều, kh&ocirc;ng bị ch&aacute;y đen. Sau đ&oacute; gọt vỏ b&iacute; rồi cắt ra th&agrave;nh từng miếng, cho v&agrave;o m&aacute;y &eacute;p hoặc gi&atilde; nhỏ vắt lấy nước. Nấu một ấm nước s&ocirc;i, cho phần x&aacute;c b&iacute; đao v&agrave;o, nấu s&ocirc;i l&ecirc;n một lần nữa, rồi lọc lấy phần nước, bỏ x&aacute;c b&iacute; đao, cho th&ecirc;m phần nước &eacute;p b&iacute; đao, nấu tiếp. Bỏ đường ph&egrave;n v&agrave;o, nấu đến khi đường tan, chia uống nhiều lần trong ng&agrave;y.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">Giải kh&aacute;t, ph&ograve;ng mụn nhọt, lở ngứa, r&ocirc;m sảy:<br style="box-sizing: border-box;" />D&ugrave;ng nước &eacute;p b&iacute; đao th&ecirc;m v&agrave;i hạt muối uống nhiều lần trong ng&agrave;y.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">Giải độc do ngộ độc thức ăn (t&ocirc;m, c&aacute;) trong trường hợp ngộ độc nhẹ:<br style="box-sizing: border-box;" />nước &eacute;p b&iacute; đao c&ocirc;ng dụng 5</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">Lấy thật nhiều nước &eacute;p b&iacute; đao để uống sẽ c&oacute; t&aacute;c dụng giải độc. nhưng sau khoảng 2 giờ nếu vẫn c&oacute; triệu chứng ngộ độc cần đến cơ sở y tế điều trị.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">Chữa sốt n&oacute;ng, h&aacute;o kh&aacute;t do nhiệt:<br style="box-sizing: border-box;" />nước &eacute;p b&iacute; đao c&ocirc;ng dụng 6</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">B&iacute; đao một quả, nướng cho ch&iacute;n vỏ ngo&agrave;i, rửa sạch, gi&atilde; n&aacute;t, cho v&agrave;o t&uacute;i vải sạch, vắt kiệt lấy nước, th&ecirc;m v&agrave;i hạt muối h&ograve;a tan, chia uống nhiều lần trong ng&agrave;y.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">C&aacute;c t&aacute;c dụng kh&aacute;c:<br style="box-sizing: border-box;" />C&aacute;c nghi&ecirc;n cứu cho thấy h&agrave;m lượng natri trong b&iacute; đao rất thấp n&ecirc;n c&oacute; t&aacute;c dụng trị liệu cho những người mắc c&aacute;c chứng bệnh như: Xơ cứng động mạch, tiểu đường, bệnh động mạch v&agrave;nh tim, vi&ecirc;m thận, ph&ugrave; thũng, bệnh cao huyết &aacute;p v&agrave; b&eacute;o ph&igrave;.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">Những lưu &yacute; khi sử dụng nước &eacute;p b&iacute; đao:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">- &nbsp; &nbsp;b&iacute; đao c&oacute; t&iacute;nh m&aacute;t n&ecirc;n đối với những người c&oacute; cơ địa lạnh th&igrave; n&ecirc;n d&ugrave;ng liều lượng dần dần &iacute;t một rồi tăng dần để cơ thể dễ th&iacute;ch nghi. Những trường hợp t&igrave; vị hư h&agrave;n, hay bị chướng bụng, ti&ecirc;u chảy kh&ocirc;ng n&ecirc;n d&ugrave;ng nhiều.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">- &nbsp; &nbsp;c&aacute;c loại nước &eacute;p b&iacute; đao chế biến sẵn thường chứa đường (sẽ sinh ra năng lượng), khi uống nhiều sẽ nạp một lượng năng lượng đ&aacute;ng kể v&agrave;o cơ thể n&ecirc;n sẽ kh&oacute; giảm c&acirc;n. V&igrave; vậy để an to&agrave;n v&agrave; hiệu quả nhất c&aacute;c chị em n&ecirc;n tự chế biến nước uống b&iacute; đao cho m&igrave;nh.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">- &nbsp; &nbsp;C&oacute; thể kết hợp nước &eacute;p b&iacute; đao với dứa, dưa hấu cũng sẽ rất ngon miệng đấy.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5;">Nước &eacute;p b&iacute; đao c&oacute; rất nhiều c&ocirc;ng dụng phải kh&ocirc;ng n&agrave;o, đặc biệt lại rất tốt cho chị em phụ nữ nữa. C&aacute;c bạn h&atilde;y thử kiểm chứng nh&eacute;.</p>\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n</div>', '17000', 15000, 'bi-dao', '2016-04-25 05:36:36', 3, 1),
(16, 'BẮP CẢI', 'bapcai.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Bắp cải c&oacute; t&aacute;c dụng bổ sung chất xơ tốt cho hệ ti&ecirc;u h&oacute;a, tốt cho qu&aacute; tr&igrave;nh chuyển h&oacute;a xương v&agrave; c&ograve;n c&oacute; khả năng ngăn ngừa bệnh Alzheimer, chống suy nhược thần kinh, giảm đau nhức, ph&ograve;ng chống c&aacute;c bệnh ung thư, tim mạch...</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;"><br style="box-sizing: border-box;" />&nbsp;Trong bắp cải c&oacute; chứa lượng vitamin cao hơn nhiều so với c&aacute;c loại rau củ kh&aacute;c như: gấp 4,5 lần so với c&agrave; rốt, 3,6 lần so với khoai t&acirc;y, h&agrave;nh t&acirc;y.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">&nbsp; Điều đặc biệt l&agrave; vitamin A v&agrave; P trong cải bắp kết hợp với nhau l&agrave;m cho th&agrave;nh mạch m&aacute;u bền vững hơn. Trong cải bắp c&ograve;n c&oacute; c&aacute;c chất chống ung thư: Sulforaphane, phenethyl isothiocyanate v&agrave; Indol-33 carbinol.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Bắp cải vừa l&agrave; một nguồn thực phẩm bổ sung chất xơ, vừa c&oacute; rất nhiều vitamin, ax&iacute;t amin v&agrave; kho&aacute;ng chất rất tốt cho xương như &nbsp;mangan, sắt v&agrave; magi&ecirc;. &nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Đặc biệt, vitamin K trong bắp cải gi&uacute;p qu&aacute; tr&igrave;nh chuyển h&oacute;a xương v&agrave; c&ograve;n c&oacute; khả năng ngăn ngừa bệnh Alzheimer. N&ecirc;n đ&acirc;y l&agrave; loại thực phẩm m&agrave; người cao tuổi v&agrave; người bị lo&atilde;ng xương kh&ocirc;ng n&ecirc;n bỏ qua.&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/bapcai-2.jpg?v=1457541971463" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Theo c&aacute;c nghi&ecirc;n cứu, ăn c&aacute;c loại rau họ cải c&oacute; thể gi&uacute;p giảm nguy cơ một số loại ung thư, trong đ&oacute; c&oacute; ung thư v&uacute; bởi trong bắp cải c&oacute; một nh&oacute;m hoạt chất indol. Qua thực nghiệm cho thấy trong chất n&agrave;y l&agrave;m giảm tỷ lệ ung thư v&uacute;.&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">C&aacute;c nh&agrave; khoa học của trường đại học Michigan đ&atilde; kết luận rằng những phụ nữ ăn 4-5 bữa bắp cải/tuần sẽ giảm được 74% nguy cơ mắc chứng bệnh ung thư v&uacute;. &nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">&nbsp;Cải bắp c&oacute; t&aacute;c dụng hạ cholesterol trong m&aacute;u, giảm nguy cơ vữa xơ mạch m&aacute;u, thiểu năng mạch v&agrave;nh, nhồi m&aacute;u cơ tim v&agrave; tai biến mạch m&aacute;u n&atilde;o.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">&nbsp;C&aacute;c chất trong bắp cải c&oacute; t&aacute;c dụng l&agrave;m giảm qu&aacute; tr&igrave;nh đồng h&oacute;a glucid v&agrave; giảm đường huyết, v&igrave; thế c&oacute; t&aacute;c dụng ph&ograve;ng bị tiểu đường tu&yacute;p 2.&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Mặt kh&aacute;c, rau bắp cải c&oacute; t&aacute;c dụng ngăn chặn glucid chuyển h&oacute;a th&agrave;nh lipid (chất b&eacute;o) l&agrave; một trong những nguy&ecirc;n nh&acirc;n g&acirc;y tăng c&acirc;n, b&eacute;o ph&igrave;.&nbsp;</p>', '15000', 12000, 'bap-cai', '2016-04-25 05:37:22', 3, 0),
(17, 'THỊT NẠC VAI LỢN', 'vaiheo.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Thịt lợn l&agrave; thực phẩm &iacute;t khi thiếu trong c&aacute;c bữa cơm hằng ng&agrave;y của ch&uacute;ng ta, nhưng l&agrave;m sao để đảm bảo được dinh dưỡng v&agrave; chất lượng thịt khi tr&ecirc;n thị trường xuất hiện h&agrave;ng loạt những cơ sở bu&ocirc;n b&aacute;n kh&ocirc;ng uy t&iacute;n, lấy lợi nhuận l&ecirc;n h&agrave;ng đầu?</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/vaiheo-4.jpg?v=1456497783687" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Sản phẩm:&nbsp;Thịt lợn&nbsp;tươi</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Loại thịt: Thịt nạc vai, nạc vai lợn</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Chất lượng: Thượng hạng</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Trọng lượng: T&ugrave;y loại</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ C&oacute; giấy chứng nhận an to&agrave;n thực phẩm</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ T&igrave;nh trạng bảo quản: Thịt&nbsp;lợn sạch, tươi, chưa qua đ&ocirc;ng lạnh</p>', '150000', 120000, 'thit-nat-vai-lon', '2016-04-25 05:39:05', 4, 1),
(18, 'TRỨNG GÀ SẠCH', 'trungga.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Trứng g&agrave; từ l&acirc;u đ&atilde; được xem l&agrave; một trong những thực phẩm bổ dưỡng. Tuy nhi&ecirc;n, lựa mua những quả trứng ngon kh&ocirc;ng phải chị em n&agrave;o cũng biết.<br style="box-sizing: border-box;" />Trứng g&agrave; từ l&acirc;u đ&atilde; được xem l&agrave; một trong những thực phẩm bổ dưỡng, bởi trong trứng c&oacute; chứa một lượng lớn c&aacute;c vitamin như A, D, E, B1, B2, B6, B12, folate cũng như h&agrave;m lượng canxi, photpho, magi&ecirc;, sắt v&agrave; kẽm rất tốt cho cơ thể. Nhưng việc chọn mua được những quả trứng thật ngon l&agrave; điều băn khoăn của rất nhiều chị em nội trợ.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/trungga-3.jpg?v=1456498100447" /></p>', '35000', 30000, 'trung-ga-sach', '2016-04-25 05:40:06', 4, 1),
(19, 'THỊT LỢN BA CHỈ SẠCH', 'lonbachi.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Thịt lợn ba chỉ sạch cung cấp nguồn dinh dưỡng cho bữa ăn của bạn, với chất thịt kh&ocirc;ng kh&ocirc; , kh&ocirc;ng qu&aacute; ngấy v&agrave; hợp với chi ph&iacute; cho bữa ăn hằng ng&agrave;y&nbsp;n&ecirc;n thịt ba chỉ l&agrave; sự lựa chọn đầu ti&ecirc;n của c&aacute;c chị em nội trợ.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/lonbachi-e13e578e-6943-430f-af64-bf91a159e1fa.jpg?v=1456498781650" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Sản phẩm: Thịt lợn tươi</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Loại thịt: Thịt lợn ba chỉ</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Chất lượng: Thượng hạng</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Trọng lượng: T&ugrave;y loại</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ C&oacute; giấy chứng nhận an to&agrave;n thực phẩm</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ T&igrave;nh trạng bảo quản: Thịt lợn sạch, tươi, chưa qua đ&ocirc;ng lạnh</p>', '105000', 85000, 'thit-lon-ba-chi', '2016-04-25 05:40:49', 4, 1),
(20, 'THỊT BÒ TƯƠI SỐNG', 'botuoi.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Chọn thịt b&ograve; bằng ch&iacute;nh c&aacute;c gi&aacute;c quan của bạn</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Khi đến h&agrave;ng thịt b&ograve;, bạn h&atilde;y chọn h&agrave;ng n&agrave;o c&oacute; thịt đỏ tươi (kh&ocirc;ng phải đỏ sậm nh&eacute;), khi ấn tay v&agrave;o thịt c&oacute; sự đ&agrave;n hồi tốt, sờ v&agrave;o thịt kh&ocirc;ng lạnh. M&agrave; nếu l&agrave; thịt b&ograve; để lạnh sẽ biết ngay, khi để ra ngo&agrave;i những phần thịt tiếp x&uacute;c với kh&ocirc;ng kh&iacute; sẽ c&oacute; m&agrave;u t&aacute;i, nh&igrave;n l&agrave; thấy v&agrave; sờ v&agrave;o giữa miếng thịt thấy lạnh tay.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/botuoi-84b9154e-aaa8-4e0a-9744-6f76b1b46979.jpg?v=1456498781650" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Hướng dẫn c&aacute;ch chọn thịt b&ograve; ngon v&agrave; đơn giản nhất<br style="box-sizing: border-box;" />Thịt b&ograve; tươi ngon c&oacute; m&agrave;u đỏ tươi<br style="box-sizing: border-box;" />Ph&acirc;n biệt thịt b&ograve; gi&agrave;, b&ograve; tơ</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Thịt b&ograve; gi&agrave; thớ thịt to, m&agrave;u đỏ t&iacute;m, mỡ c&oacute; m&agrave;u xam x&aacute;m c&ograve;n thịt b&ograve; tơ thớ thịt mịn, thịt mềm, m&agrave;u đỏ tươi, mỡ c&oacute; m&agrave;u trắng. Chọn thịt c&oacute; m&ugrave;i thơm, nếu thịt c&oacute; m&ugrave;i h&ocirc;i v&agrave; tr&ecirc;n thịt c&oacute; nốt hoặc m&agrave;u đỏ sậm (như kiểu thịt con g&agrave; cắt tiết chưa hết ấy) l&agrave; thịt kh&ocirc;ng ngon. Đ&oacute; l&agrave; mẹo chọn thịt b&ograve; tươi ngon v&agrave; cực chuẩn.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Ph&acirc;n biệt thịt tr&acirc;u v&agrave; thịt b&ograve;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Nếu như thịt tr&acirc;u v&agrave; thịt b&ograve; gi&agrave; đều c&oacute; đặc điểm l&agrave; thớ thịt to, c&oacute; m&agrave;u đỏ sậm hơn thịt b&ograve; non, v&igrave; vậy bạn ph&acirc;n biệt qua m&agrave;u mỡ của nh&eacute;. Thịt b&ograve; gi&agrave; mỡ sẽ c&oacute; m&agrave;u xam x&aacute;m (v&agrave;ng sậm), c&ograve;n thịt tr&acirc;u th&igrave; mỡ sẽ c&oacute; m&agrave;u trắng. Nhưng đa số nh&igrave;n thịt tr&acirc;u v&agrave; thịt b&ograve; bạn c&oacute; thể nhận biết được ngay, v&igrave; đa số thịt tr&acirc;u m&agrave;u thịt vẫn sậm hơn thịt b&ograve; gi&agrave;, nh&igrave;n v&agrave;o miếng thịt c&oacute; m&agrave;u tối hơn l&agrave; thịt b&ograve;.</p>', '270000', 250000, 'thit-bo-tuoi-song', '2016-04-25 05:41:38', 4, 1),
(21, 'THỊT GÀ SẠCH', 'thitga.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Thay đổi bữa ăn hằng ng&agrave;y của bạn mới thịt g&agrave; sạch từ freshfood sẽ l&agrave; sự lựa chọn đ&uacute;ng. Ch&uacute;ng t&ocirc;i cung cấp nguồn thực phẩm c&oacute; nguồn gốc r&otilde; r&agrave;ng, chất lượng đảm bảo lu&ocirc;n tươi ngon &nbsp;v&agrave; hợp vệ sinh.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/thitga-4.jpg?v=1456498985200" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Sản phẩm: Thịt g&agrave; tươi</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Loại thịt: C&aacute;nh g&agrave;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Chất lượng: Thượng hạng</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Trọng lượng: T&ugrave;y loại</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ C&oacute; giấy chứng nhận an to&agrave;n thực phẩm</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ T&igrave;nh trạng bảo quản: Thịt g&agrave; sạch, tươi, chưa qua đ&ocirc;ng lạnh</p>', '120000', 95000, 'thit-ga-sach', '2016-04-25 05:42:17', 4, 1),
(22, 'THỊT BÒ NHẬP KHẨU', 'thitbe.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Ba chỉ b&ograve; Mỹ v&agrave; Nạc vai b&ograve; &Uacute;c - l&agrave; m&oacute;n ko thể thiếu trong tủ lạnh. 2 em n&agrave;y đ&atilde; rất quen thuộc trong c&aacute;c nh&agrave; h&agrave;ng lẩu, nướng nổi tiếng. Thịt dễ ăn, dễ chế biến, c&oacute; thể ăn lẩu, nướng, chế biến m&oacute;n phở, x&agrave;o nấu với tất cả những g&igrave; bạn muốn.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/botuoi-84b9154e-aaa8-4e0a-9744-6f76b1b46979.jpg?v=1456498781650" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">*** L&otilde;i r&ugrave;a b&ograve; Mỹ: Thịt l&otilde;i r&ugrave;a l&agrave; h&agrave;ng hiếm, ăn rất ngon v&igrave; thịt rất ngọt, mềm mềm nhưng lại gi&ograve;n gi&ograve;n, g&acirc;n xen đều miếng thịt, đặc sản n&agrave;y d&ugrave;ng để ăn lẩu, kho nguy&ecirc;n c&aacute;i, l&agrave;m m&oacute;n l&otilde;i r&ugrave;a b&ograve; ng&acirc;m mắm nh&acirc;m nhi th&igrave; hết &yacute;.<br style="box-sizing: border-box;" />*** Thăn vai b&ograve; &Uacute;c - Rất th&iacute;ch hợp l&agrave;m m&oacute;n bittet, nướng, x&agrave;o l&uacute;c lắc...<br style="box-sizing: border-box;" />Ưu điểm của thăn vai b&ograve; &Uacute;c rất ph&ugrave; hợp l&agrave;m bittet v&agrave; x&agrave;o l&uacute;c lắc k&egrave;m c&aacute;c loại rau củ, m&agrave;u sắc đẹp m&agrave; lại ngon, thịt rất mềm, thơm, dễ ăn, c&aacute;c bạn nhỏ cũng sẽ rất th&iacute;ch n&agrave;y.<br style="box-sizing: border-box;" />*** Gầu hoa b&ograve; Mỹ th&igrave; kh&ocirc;ng cần giới thiệu th&ecirc;m về em ấy nữa, lẩu v&agrave; phở kh&ocirc;ng thể thiếu em được v&igrave; gầu b&ograve; gi&ograve;n gi&ograve;n, ngon ngon v&agrave; n&eacute;t đặc trưng ri&ecirc;ng m&agrave; phải ăn mới cảm nhận được.</p>', '270000', 250000, 'thit-bo-nhap-khau', '2016-04-25 05:43:11', 4, 1),
(23, 'CÁ THU LOẠI NGON', 'cathu.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">C&aacute; Thu&nbsp;&ndash; loại c&aacute; rất quen thuộc được nhiều người th&iacute;ch ăn bởi nguồn đạm, chất b&eacute;o&hellip; trong c&aacute; thu rất bổ dưỡng đối với sức khỏe của mọi người, từ người gi&agrave;, trẻ em, phụ nữ mang thai v&agrave; phụ nữ trong thời gian sau khi sinh. &nbsp;C&aacute; thu c&oacute; t&aacute;c dụng chữa bệnh.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Hơn nữa, c&aacute; thu c&oacute; chứa loại dầu mang t&ecirc;n omega-3 c&oacute; t&aacute;c dụng rất lớn trong việc ngăn chặn sự h&igrave;nh th&agrave;nh chất prostaglasdins c&oacute; li&ecirc;n quan đến những cơn đau khi h&agrave;nh kinh, đến chứng tổn thương ngực v&agrave; thậm ch&iacute; l&agrave; ung thư v&uacute; ở phụ nữ. Một cuộc khảo s&aacute;t nghi&ecirc;n cứu tr&ecirc;n 290 phụ nữ do Tạp ch&iacute; dinh dưỡng l&acirc;m s&agrave;ng ch&acirc;u &Acirc;u thực hiện cho biết, nếu bạn ăn c&aacute; thu đều đặn th&igrave; mỗi kỳ kinh nguyệt sẽ cảm thấy nhẹ nh&otilde;m v&agrave; thoải m&aacute;i hơn. Ngo&agrave;i ra, nghi&ecirc;n cứu của Hiệp hội sức khỏe Mỹ cũng cho thấy, omega-3 trong c&aacute; thu c&oacute; khả năng ngăn chặn sự ph&aacute;t triển của tế b&agrave;o ung thư v&uacute; v&agrave; ph&ograve;ng chữa bệnh tim mạch.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/cathu-3.jpg?v=1456496264743" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">&ndash;&nbsp;Quy c&aacute;ch:&nbsp;c&aacute; thu nguy&ecirc;n con&nbsp;&amp;&nbsp;C&aacute; thu được cắt kh&uacute;c. Mỗi Kg được cắt l&agrave;m 3 &ndash; 4 kh&uacute;c.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">&ndash;&nbsp;Nguồn gốc: Tuy Ho&agrave;- Ph&uacute; Y&ecirc;n, Nha Trang, Cam Ranh- Kh&aacute;nh Ho&agrave;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Lưu&nbsp;&yacute; : Qu&yacute; kh&aacute;ch muốn c&oacute; c&aacute; thu tươi&nbsp;đặt h&agrave;ng trước 1 ng&agrave;y.</p>', '150000', 120000, 'ca-thu-loai-ngon', '2016-04-25 05:44:14', 5, 1);
INSERT INTO `products` (`id`, `name`, `image`, `description`, `price`, `sale_price`, `slug`, `created`, `category_id`, `status`) VALUES
(24, 'BỀ BỀ LOẠI TO', 'bebe.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Bề bề tươi sống hay c&ograve;n gọi l&agrave; t&ocirc;m t&iacute;t, t&ocirc;m t&iacute;ch, l&agrave; loại thực phẩm c&oacute; gi&aacute; trị dinh dưỡng cao. T&ocirc;m t&iacute;t được cung cấp tại giadinhhaisan.com khu vực H&agrave; Nội với mức gi&aacute; ưu đ&atilde;i.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Bề bề tươi sống hay c&ograve;n gọi l&agrave; t&ocirc;m t&iacute;t, t&ocirc;m t&iacute;ch, l&agrave; loại thực phẩm c&oacute; gi&aacute; trị dinh dưỡng cao. Bề bề được cung cấp tại giadinhhaisan.com khu vực H&agrave; Nội với gi&aacute; tốt.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Bề bề tươi sống</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">T&igrave;m hiểu về con bề bề</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">C&oacute; lẽ rất nhiều người khi n&oacute;i đến con bề bề đều kh&ocirc;ng biết ch&uacute;ng l&agrave; con g&igrave;? Theo t&agrave;i liệu cũng như thực chứng ở nhiều địa phương th&igrave; ch&uacute;ng t&ocirc;i c&oacute; thể đem đến cho c&aacute;c bạn những th&ocirc;ng tin về lo&agrave;i hải sản n&agrave;y như sau:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Bề bề hay c&ograve;n c&oacute; những c&aacute;i t&ecirc;n kh&aacute;c như: t&ocirc;m t&iacute;t, t&ocirc;m t&iacute;ch, t&ocirc;m thuyền v&agrave; một số địa phương gọi ch&uacute;ng l&agrave; con t&ocirc;m b&uacute;a. Con bề bề thuộc nh&oacute;m gi&aacute;p x&aacute;c biển thuộc bộ T&ocirc;m, ch&acirc;n miệng c&oacute; thuật ngữ tiếng anh l&agrave; (Stomatopoda). Bề bề nh&igrave;n vừa giống t&ocirc;m v&agrave; vừa giống bọ ngựa, trong tiếng Anh ch&uacute;ng được đặt t&ecirc;n l&agrave; Mantis shrimp. Ch&uacute;ng sống ở rất nhiều v&ugrave;ng biển tr&ecirc;n thế giới từ &ocirc;n đới v&agrave; nhiệt đới tr&ecirc;n to&agrave;n cầu v&agrave; ở Việt Nam tjif ch&uacute;ng được d&ugrave;ng l&agrave;m thực phẩm c&oacute; t&ecirc;n l&agrave; Squilidae trong bộ t&ocirc;m ch&acirc;n miệng.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/bebe-3.jpg?v=1456496589787" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Bề bề c&oacute; thể d&agrave;i đến 30 cm, một số con c&oacute; thể d&agrave;i hơn, m&agrave;u sắc b&ecirc;n ngo&agrave;i của ch&uacute;ng c&oacute; thể thay đổi t&ugrave;y từng v&ugrave;ng biển, từ xanh lục cho đến hồng v&agrave; v&agrave;ng nhạt..Bề bề c&oacute; 8 đ&ocirc;i ch&acirc;n, trong số đ&oacute; c&oacute; 5 đ&ocirc;i đầu c&oacute; c&agrave;ng, số c&ograve;n lại d&ugrave;ng để bơi dưới m&ocirc;i trường nước. Mắt con bề bề được xem l&agrave; phức tạp nhất trong giới động vật, ch&uacute;ng c&oacute; thể ph&acirc;n biệt được c&aacute;c dạng san h&ocirc;, động vật,..</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Bề bề thường sống v&ugrave;i th&acirc;n m&igrave;nh trong c&aacute;t hoặc c&aacute;c kẽ đ&aacute;, ch&uacute;ng sống bằng việc ăn c&aacute;c động vậy nhỏ hơn thậm ch&iacute; cả c&aacute;, bề bề d&ugrave;ng c&agrave;ng giữa để bắt giữ con mồi.</p>', '250000', 200000, 'be-be-loai-to', '2016-04-25 05:45:13', 5, 1),
(25, 'TÔM HÙM LOẠI 1', 'tom.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">T&ocirc;m thẻ nhiều chất dinh dưỡng, rất ngon, thịt săn chắc v&agrave; ngọt, dễ chế biến v&agrave; mang hương thơm đậm đ&agrave; đặc trưng. Loại to, từ....con đến....con/ kg. Đạt chuẩn xuất khẩu ch&acirc;u &Acirc;u, sạch. Giao h&agrave;ng 2 lần tại H&agrave; Nội.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/tom-2.jpg?v=1456496954320" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Điểm nổi bật của t&ocirc;m thẻ Yến Thảo:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">T&ocirc;m thẻ sống khỏe 100%</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Nguồn gốc: nu&ocirc;i tại&hellip;&hellip;&hellip;&hellip;..</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">K&iacute;ch cỡ:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Loại 1 từ&hellip;- ...con/ kg</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Loại 2 từ&hellip;-&hellip;.con/kg</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Nu&ocirc;i theo quy tr&igrave;nh đạt chuẩn xuất khẩu Ch&acirc;u &Acirc;u, sạch, thịt ngọt, dai v&agrave; thơm.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Đ&aacute;p ứng được nhu cầu của c&aacute;c nh&agrave; h&agrave;ng, si&ecirc;u thị lớn tại H&agrave; Nội</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">H&agrave;ng nhiều, đều đặn, đ&uacute;ng hẹn</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Giao h&agrave;ng 2 lần/ ng&agrave;y tại H&agrave; Nội ( S&aacute;ng &ndash; chiều)</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Đặt mua h&agrave;ng:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Nh&agrave; h&agrave;ng, si&ecirc;u thị c&oacute; nhu cầu li&ecirc;n hệ đặt trước 1 ng&agrave;y.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Giao h&agrave;ng 2 lần/ 1 ng&agrave;y (s&aacute;ng v&agrave; chiều).</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Gi&aacute; &aacute;p dụng cho kh&aacute;ch h&agrave;ng mua lẻ. Nh&agrave; h&agrave;ng, si&ecirc;u thị mua số lượng lớn li&ecirc;n hệ trực tiếp để được b&aacute;o gi&aacute; cụ thể.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Kh&aacute;ch lẻ, Yến Thảo sẽ nhận đặt h&agrave;ng trước 1 ng&agrave;y .</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Khuyến kh&iacute;ch kh&aacute;ch lẻ tới trực tiếp mua h&agrave;ng.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Th&ocirc;ng tin giới thiệu</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">T&ocirc;m thẻ vỗn l&agrave; lo&agrave;i tự nhi&ecirc;n kh&aacute; phổ biến ở v&ugrave;ng biển c&aacute;c tỉnh Miền T&acirc;y v&agrave; đặc biệt l&agrave; C&agrave; Mau. Tuy nhi&ecirc;n, hiện nay ch&uacute;ng được nu&ocirc;i th&agrave;nh c&ocirc;ng ở nhiều nơi, trong đ&oacute; khu vực&hellip;&hellip;&hellip;&hellip; được xem một trong những nơi c&oacute; điều kiện tự nhi&ecirc;n thuận lợi, th&iacute;ch hợp v&agrave; cho chất lượng thịt tốt nhất.&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">T&ocirc;m thẻ nhiều chất dinh dưỡng, rất ngon, thịt săn chắc v&agrave; ngọt, dễ chế biến v&agrave; mang hương thơm đậm đ&agrave; đặc trưng.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Kh&ocirc;ng chỉ l&agrave; một loại thực phẩm gi&agrave;u dinh dưỡng m&agrave; theo y học cổ truyền, t&ocirc;m c&oacute; vị ngọt, t&iacute;nh ấm, c&oacute; c&ocirc;ng dụng bổ thận tr&aacute;ng dương, th&ocirc;ng sữa, khử độc.</p>', '120000', 105000, 'tom-hum-loai-1', '2016-04-25 05:46:24', 5, 1),
(28, 'MỰC ỐNG', 'muc.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Mực ống th&acirc;n cuộn tr&ograve;n (h&igrave;nh ống), hơi d&agrave;i, m&igrave;nh mỏng hơn so với mực nang. Vốn được biết đến l&agrave; một trong những đặc sản biển nổi tiếng với hương vị thơm ngon, ngọt tự nhi&ecirc;n, cho gi&aacute; trị thương mại cao được thị trường EU v&agrave; Nhật Bản ưa chuộng.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/muc-3.jpg?v=1456497515940" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">PHẦN ĐẦU:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Mực ống được đ&aacute;nh bắt trực tiếp từ biển Phan Thiết, Ph&uacute; Quốc. Bảo quản bằng phương ph&aacute;p cấp đ&ocirc;ng ngay tr&ecirc;n t&agrave;u, t&igrave;nh trạng tươi, ngon, thịt chắc, giữ được vị ngọt đặc trưng. Giao h&agrave;ng tại H&agrave; Nội 2 lần/ 1 ng&agrave;y.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">ĐẶC ĐIỂM NỔI BẬT</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Mực ống được đ&aacute;nh bắt trực tiếp từ biển Phan Thiết, Ph&uacute; Quốc<br style="box-sizing: border-box;" />Mực được cấp đ&ocirc;ng ngay tr&ecirc;n t&agrave;u, t&igrave;nh trạng tươi, ngon, thịt chắc, giữ được vị ngọt đặc trưng<br style="box-sizing: border-box;" />C&oacute; đủ size v&agrave; quy c&aacute;ch để lựa chọn<br style="box-sizing: border-box;" />Loại 1: từ 5-7 con/kg,<br style="box-sizing: border-box;" />Loại 2: từ 2-3 con/kg hoặc nhỏ hơn...<br style="box-sizing: border-box;" />Chiều d&agrave;i &nbsp;c&oacute; 3 lựa chọn:<br style="box-sizing: border-box;" />20-25 cm<br style="box-sizing: border-box;" />25-30 cm<br style="box-sizing: border-box;" />tr&ecirc;n 30 cm<br style="box-sizing: border-box;" />ĐẶT MUA H&Agrave;NG</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Nh&agrave; h&agrave;ng, si&ecirc;u thị c&oacute; nhu cầu li&ecirc;n hệ đặt trước 1 ng&agrave;y.<br style="box-sizing: border-box;" />Giao h&agrave;ng 2 lần/ 1 ng&agrave;y (s&aacute;ng v&agrave; chiều).<br style="box-sizing: border-box;" />Gi&aacute; &aacute;p dụng cho kh&aacute;ch h&agrave;ng mua lẻ. Nh&agrave; h&agrave;ng, si&ecirc;u thị mua số lượng lớn li&ecirc;n hệ trực tiếp để được b&aacute;o gi&aacute; cụ thể.<br style="box-sizing: border-box;" />Kh&aacute;ch lẻ, Yến Thảo sẽ nhận đặt h&agrave;ng trước 1 ng&agrave;y .<br style="box-sizing: border-box;" />Khuyến kh&iacute;ch kh&aacute;ch lẻ tới trực tiếp mua h&agrave;ng.<br style="box-sizing: border-box;" />TH&Ocirc;NG TIN GIỚI THIỆU</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Mực ống th&acirc;n cuộn tr&ograve;n (h&igrave;nh ống), hơi d&agrave;i, m&igrave;nh mỏng hơn so với mực nang. Vốn được biết đến l&agrave; một trong những đặc sản biển nổi tiếng với hương vị thơm ngon, ngọt tự nhi&ecirc;n, cho gi&aacute; trị thương mại cao được thị trường EU v&agrave; Nhật Bản ưa chuộng.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Mực ống Yến Thảo<br style="box-sizing: border-box;" />X&eacute;t về th&agrave;nh phần dinh dưỡng, trong 100g mực c&oacute; chứa 13g chất đạm, 0,7g chất b&eacute;o, nhiềucanxi, phospho, sắt&hellip; v&agrave; c&aacute;c vitamin B1, B2, PP, l&agrave; thực phẩm l&agrave;nh t&iacute;nh, d&ugrave;ng tốt cho nhiều đối tượng kể cả trẻ nhỏ v&agrave; phụ nữ trong thai kỳ.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Lưu &yacute; khi chọn mực ống th&igrave; n&ecirc;n chọn những con c&oacute; lớp thịt m&agrave;u s&aacute;ng hơi hồng. Phần đầu d&iacute;nh chặt v&agrave;o th&acirc;n, t&uacute;i mực chưa bị vỡ. &nbsp;Mực k&eacute;m tươi nếu xuất hiện hiện tượng chuyển m&agrave;u hơi xanh ng&agrave;, thịt nh&atilde;o, đầu kh&ocirc;ng d&iacute;nh chặt với th&acirc;n, m&ugrave;i rất tanh.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Mực ống c&oacute; thể chế biến được nhiều m&oacute;n ăn ngon v&agrave; đa dạng. Tuy nhi&ecirc;n hai m&oacute;n th&ocirc;ng dụng nhất l&agrave; l&agrave;m kh&ocirc; mực v&agrave; nhồi thịt.&nbsp;</p>', '170000', 150000, 'muc-ong', '2016-04-25 05:52:11', 5, 1),
(29, 'CUA CÀ MAU', 'cua.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Cua biển C&agrave; Mau ch&iacute;nh gốc, vận chuyển bảo quản sống 100% ra H&agrave; Nội.Cua c&oacute; đủ loại: cua đực, cua gạch,....Cua chất lượng cao, thịt chắc, gạch đầy, ngọt đậm. Giao h&agrave;ng 2 lần/ng&agrave;y tại H&agrave; Nội. H&agrave;ng đều đặn hằng ng&agrave;y. Đ&aacute;p ứng nhu cầu nh&agrave; h&agrave;ng, si&ecirc;u thị lớn.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/cua-2.jpg?v=1456497278093" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Điểm nổi bật của tu h&agrave;i Yến Thảo:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Cua biển C&agrave; Mau ch&iacute;nh hiệu, được vận chuyển v&agrave; bảo quản theo đường h&agrave;ng kh&ocirc;ng bằng phương thức đặc biệt. T&igrave;nh trạng sống khỏe 100% khi giao h&agrave;ng.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">K&iacute;ch thước + c&acirc;n nặng: đầy đủ c&aacute;c size từ 100-300gram/con</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">C&oacute; 3 loại:&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Cua gạch.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Cua thịt.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Cua cốm&hellip;.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Gi&aacute; của 3 loại n&agrave;y thay đổi t&ugrave;y thuộc y&ecirc;u cầu của kh&aacute;ch h&agrave;ng. Gi&aacute; tr&ecirc;n để chỉ nhằm mục đ&iacute;ch tham khảo.<br style="box-sizing: border-box;" />Cua đều con, thịt chắc, m&igrave;nh d&agrave;y, nhiều thịt, ngọt đậm đ&agrave;, gạch đầy.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">D&acirc;y buộc: Buộc theo y&ecirc;u cầu của kh&aacute;ch h&agrave;ng.&nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Giao h&agrave;ng 2 lần tại H&agrave; Nội/ ng&agrave;y ( S&aacute;ng &ndash; chiều).</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Đặt mua h&agrave;ng:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Nh&agrave; h&agrave;ng, si&ecirc;u thị c&oacute; nhu cầu li&ecirc;n hệ đặt trước 1 ng&agrave;y.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Giao h&agrave;ng 2 lần/ 1 ng&agrave;y (s&aacute;ng v&agrave; chiều).</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Gi&aacute; &aacute;p dụng cho kh&aacute;ch h&agrave;ng mua lẻ. Nh&agrave; h&agrave;ng, si&ecirc;u thị mua số lượng lớn li&ecirc;n hệ trực tiếp để được b&aacute;o gi&aacute; cụ thể.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Kh&aacute;ch lẻ, Yến Thảo sẽ nhận đặt h&agrave;ng trước 1 ng&agrave;y .</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Khuyến kh&iacute;ch kh&aacute;ch lẻ tới trực tiếp mua h&agrave;ng.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Th&ocirc;ng tin giới thiệu</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Do điều kiện tự nhi&ecirc;n kh&aacute;c nhau n&ecirc;n mỗi v&ugrave;ng miền c&oacute; một sản vật ri&ecirc;ng, độc đ&aacute;o. N&oacute;i về chất lượng v&agrave; m&ugrave;i vị của cua biển th&igrave; kh&ocirc;ng đ&acirc;u c&oacute; thể s&aacute;nh bằng cua C&agrave; Mau. Cua C&agrave; Mau l&agrave; một l&ograve;ai đặc sản tuyệt vời, qu&agrave; tặng từ thi&ecirc;n nhi&ecirc;n mang đến cho con người, v&agrave; n&oacute; gần như l&agrave; m&oacute;n sản vật ngon nhất ở ch&acirc;u &Aacute;. Người Trung Quốc c&ograve;n xem cua Việt qu&yacute; như v&agrave;ng.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Mỗi loại cua c&agrave; mau: cua gạch, cua yếm vu&ocirc;ng, cua đực,... đều c&oacute; vị ngon v&agrave; bổ dưỡng ri&ecirc;ng. Đặc trưng dễ nhận thấy của của C&agrave; Mau đ&oacute; l&agrave; thịt chắc v&agrave; đầy do đ&oacute; lu&ocirc;n nặng hơn c&aacute;c loại cua c&ugrave;ng trang lứa ở c&aacute;c v&ugrave;ng kh&aacute;c. C&agrave;ng cua loại n&agrave;y thường rất to, thịt đầy, ngọt đậm đ&agrave;, thơm, dai c&oacute; rất nhiều dinh dưỡng v&agrave; rất tốt cho mọi đối tượng. Nếu l&agrave; cua gạch th&igrave; c&oacute; th&ecirc;m đặc trưng gạch son nhiều hơn hẳn, khi ăn cảm nhận được vị b&eacute;o ngậy, ng&acirc;y ngất đầu lưỡi.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Để mua được cua C&agrave; Mau ch&iacute;nh hiệu, khi mua kh&aacute;ch h&agrave;ng lưu &yacute;, &nbsp;cua thật khi bẻ ra kh&ocirc;ng &oacute;p. B&ecirc;n trong th&acirc;n cua kh&ocirc;ng chứa đầy nước v&agrave; nh&atilde;o, mềm như c&aacute;c loại cua giả. Lớp da thẳng b&oacute;ng, đ&oacute; l&agrave; cua mập. &nbsp;B&oacute;p yếm cảm thấy cứng tay l&agrave; cua chắc hoặc khi b&oacute;p vừa tay, nếu thấy cua gi&atilde;y giụa to&agrave;n bộ que, c&agrave;ng th&igrave; cua c&ograve;n khỏe... &nbsp;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Ngo&agrave;i ra, cua biển C&agrave; Mau c&ograve;n l&agrave; một loại thực phẩm tươi sống rất tốt cho sức khỏe cũng như về mặt y học. Cua biển c&ograve;n chữa được một số bệnh như: c&ograve;i xương, đ&aacute;i dầm ở trẻ em, sinh huyết t&aacute;n ứ, đau bụng ở phụ nữ...</p>', '70000', 50000, 'cua-ca-mau', '2016-04-25 05:53:43', 5, 1),
(30, 'NHO KHÔNG HẠT', 'nho.jpg', '<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Nho rất tốt cho mẹ bầu v&agrave; thai nhi</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Đầu ti&ecirc;n, h&atilde;y xem trong 1 quả nho tươi gồm c&oacute; những th&agrave;nh phần n&agrave;o:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">- Quả nho: Chứa từ 10 &ndash; 33% đường (fructose &amp; glucose); 65 &ndash; 85% l&agrave; nước. Ngo&agrave;i ra, n&oacute; c&ograve;n chứa nhiều th&agrave;nh phần kh&aacute;c như: Vitamin B1, B2, B6, B12, acid folic, magie, canxi, acid galic, sắt&hellip;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;"><br style="box-sizing: border-box;" />- Hạt nho: Chứa dầu b&eacute;o, vani, hợp chất tanin, lecithin&hellip;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">- Vỏ nho: Chứa hợp chất tanin v&agrave; dầu cần thiết.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: center;"><img style="box-sizing: border-box; border: 0px; vertical-align: middle;" src="https://bizweb.dktcdn.net/100/053/074/products/nho-4.jpg?v=1457542270730" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">1. C&ocirc;ng dụng tuyệt vời của quả nho tươi</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">- Đối với mẹ:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Cung cấp nước cho cơ thể: Nước v&ocirc; c&ugrave;ng quan trọng với cơ thể nhất l&agrave; đối với phụ nữ mang thai, trong khi quả nho tươi c&oacute; chứa gần 85% l&agrave; nước. Do đ&oacute;, ăn nho hoặc uống nước &eacute;p nho thường xuy&ecirc;n sẽ gi&uacute;p mẹ bổ sung lượng nước v&agrave; chất dinh dưỡng cần thiết cho cơ thể.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Trong quả nho c&oacute; chứa 85% l&agrave; nước </p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Gi&uacute;p cơ thể giải độc: Quả nho c&oacute; chứa nhiều nước v&agrave; kali gi&uacute;p lợi tiểu th&uacute;c đẩy nhanh qu&aacute; tr&igrave;nh giải độc cho cơ thể, ngăn ngừa sỏi thận...</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Hạn chế t&aacute;o b&oacute;n: Ăn nho thường xuy&ecirc;n mẹ sẽ hạn chế gặp căn bệnh &ldquo;kh&oacute; n&oacute;i&rdquo; m&agrave; bất cứ mẹ bầu n&agrave;o cũng gặp phải, do nho chứa kh&aacute; nhiều chất xơ tốt cho hệ ti&ecirc;u h&oacute;a.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Giảm chuột r&uacute;t: Mẹ bầu sẽ tr&aacute;nh được những kh&oacute; chịu do bị chuột r&uacute;t g&acirc;y ra bởi lượng magi&ecirc; kh&aacute; gi&agrave;u trong quả nho tươi.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Tăng khả năng miễn dịch: Quả nho chứa rất nhiều vitamin v&agrave; kho&aacute;ng chất, điển h&igrave;nh l&agrave; vitamin C (100gram nho c&oacute; chứa 11mg vitamin C) gi&uacute;p mẹ tăng cường hệ miễn dịch cho cơ thể. Ngo&agrave;i ra, trong nho c&ograve;n chứa c&aacute;c chất chống oxy h&oacute;a như geraniol, nerol, flavon&hellip; c&oacute; t&aacute;c dụng chống nhiễm tr&ugrave;ng.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Kiểm so&aacute;t qu&aacute; tr&igrave;nh trao đổi chất gi&uacute;p con hấp thu chất dinh dưỡng tốt hơn: Điều n&agrave;y do c&aacute;c vitamin B c&oacute; rất nhiều trong nho &ldquo;đảm nhiệm&rdquo;.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Gi&uacute;p cơ thể kiểm so&aacute;t tốt lượng cholesterol trong qu&aacute; tr&igrave;nh mang thai do quả nho c&oacute; chưa hợp chất resveratrol (một th&agrave;nh phần rất quan trọng c&oacute; trong quả nho).</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Tốt cho qu&aacute; tr&igrave;nh chuyển dạ: Vitamin E v&agrave; K c&oacute; trong quả nho c&oacute; t&aacute;c dụng gi&uacute;p đ&ocirc;ng m&aacute;u, rất c&oacute; lợi cho qu&aacute; tr&igrave;nh chuyển dạ của mẹ bầu. Chưa kể, d&ugrave;ng nước &eacute;p nho trước khi chuyển dạ c&ograve;n gi&uacute;p mẹ bầu chống lại sự mệt mỏi, căng thẳng hiệu quả.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Ngo&agrave;i ra, l&aacute; nho c&ograve;n được d&ugrave;ng để điều trị chảy m&aacute;u tử cung.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">- Đối với thai nhi:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Ngăn ngừa khuyết tật ống thần kinh: Trong quả nho c&oacute; chứa rất nhiều acid folic gi&uacute;p thai nhi hạn chế nguy cơ khuyết tật ống thần kinh, giảm nguy cơ mắc c&aacute;c dị tật như hở h&agrave;m ếch, sứt m&ocirc;i.. B&ecirc;n cạnh đ&oacute;, h&agrave;m lượng natri v&agrave; kali c&oacute; trong nước nho c&ograve;n gi&uacute; cho hệ thần kinh của thai nhi ph&aacute;t triển to&agrave;n diện.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Tốt cho thị lực: Trong qu&aacute; tr&igrave;nh mang thai nếu mẹ t&iacute;ch cực ăn nho sẽ gi&uacute;p con sinh ra c&oacute; đ&ocirc;i mắt s&aacute;ng v&igrave; trong nho c&oacute; chứa hợp chất flavonol v&agrave; nhiều vitamin A.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">+ Ho&agrave;n thiện gene: L&agrave; một phần của axit nucleic n&ecirc;n phốt pho c&oacute; trong nho gi&uacute;p thai nhi ho&agrave;n thiện c&aacute;c gene.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">Những lưu &yacute; khi mẹ ăn nho:</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.5; color: #35240d; font-family: ''Roboto Slab'', sans-serif; font-size: 14px; text-align: justify;">- N&ecirc;n chọn nho c&oacute; nguồn gốc ăn to&agrave;n, trước tri ăn rửa thật sạch dưới v&ograve;i nước<br style="box-sizing: border-box;" />- Phần vỏ nho hơi kh&oacute; ti&ecirc;u n&ecirc;n tốt nhất mẹ lột bỏ phần n&agrave;y trước khi ăn, chưa kể n&oacute; c&oacute; thể g&acirc;y t&aacute;o b&oacute;n.<br style="box-sizing: border-box;" />- Sau khi ăn c&aacute;, uống sữa, uống nước kho&aacute;ng&hellip; mẹ bầu kh&ocirc;ng n&ecirc;n tr&aacute;ng miệng bằng nho v&igrave; c&oacute; thể khiến dạ d&agrave;y kh&oacute; chịu<br style="box-sizing: border-box;" />- Mẹ bầu bị kiết lỵ, ti&ecirc;u chảy, đau bao tử, tiểu đường kh&ocirc;ng n&ecirc;n ăn nho.<br style="box-sizing: border-box;" />- Tương tự, với những mẹ bị s&acirc;u răng n&ecirc;n hạn chế ăn nho, hoặc sau khi ăn n&ecirc;n x&uacute;c miệng thật kỹ để ngừa s&acirc;u răng v&igrave; lượng đường trong nho kh&aacute; cao, từ 10 &ndash; 33% đường (fructose &amp; glucose).</p>', '95000', 59000, 'nho-khong-hat', '2016-04-26 03:56:36', 2, 1),
(31, 'Nước Cam Ép', '2512355orange-juice-1.jpg', '<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Nước cam &eacute;p l&agrave; nguồn cung cấp vitamin C dồi d&agrave;o gi&uacute;p th&uacute;c đẩy qu&aacute; tr&igrave;nh giải độc &amp; tăng sức đề kh&aacute;ng cho cơ thể.</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Hợp chất Citrus Limonoid c&oacute; trong cam &eacute;p sẽ gi&uacute;p ph&ograve;ng chống ung thư v&uacute; v&agrave; ung thư phổi.</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Ngo&agrave;i ra nước cam &eacute;p c&ograve;n gi&uacute;p tăng cường độ đ&agrave;n hồi của th&agrave;nh mạch, gi&uacute;p ngăn ngừa c&aacute;c trường hợp bị xơ vữa động mạch &amp; tăng huyết &aacute;p.</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Chỉ một ly nước cam tươi c&oacute; thể cung cấp hơn 50% nhu cầu về vitamin C cho một phụ nữ cần trong ng&agrave;y.</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Nước cam tươi c&ograve;n l&agrave; nguồn cung cấp phong ph&uacute; thiamine v&agrave; folate.</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Thiamine tham gia v&agrave;o qu&aacute; tr&igrave;nh tổng hợp năng lượng, c&ograve;n folate gi&uacute;p gia tăng chất lượng m&aacute;u, chất n&agrave;y cũng đ&oacute;ng vai tr&ograve; quan trọng cho phụ nữ mang thai hay những người muốn c&oacute; con v&igrave; đ&oacute;ng vai tr&ograve; quan trọng cho việc ngăn ngừa những khiếm khuyết như bệnh nứt đốt sống ở b&agrave;o thai (spina-bifida).</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px; text-align: center;"><img style="max-width: 100%; height: 500px; vertical-align: middle; border: 0px; width: 500px;" src="http://media.bizwebmedia.net/Sites/68297/data/upload/orange_juice.jpg" alt="" /></p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Thiamine tham gia v&agrave;o qu&aacute; tr&igrave;nh tổng hợp năng lượng, c&ograve;n folate gi&uacute;p gia tăng chất lượng m&aacute;u, chất n&agrave;y cũng đ&oacute;ng vai tr&ograve; quan trọng cho phụ nữ mang thai hay những người muốn c&oacute; con v&igrave; đ&oacute;ng vai tr&ograve; quan trọng cho việc ngăn ngừa những khiếm khuyết như bệnh nứt đốt sống ở b&agrave;o thai (spina-bifida).</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Với chế độ ăn uống nhiều chất b&eacute;o v&agrave; đường, trong cơ thể sẽ h&igrave;nh th&agrave;nh sự sưng vi&ecirc;m để phản ứng lại. Sự sưng vi&ecirc;m n&agrave;y l&agrave; nguy&ecirc;n nh&acirc;n gi&aacute;n tiếp dẫn đến sự l&agrave;m cứng th&agrave;nh mạch m&aacute;u do sự đọng chất b&eacute;o, v&agrave; cũng g&acirc;y ra bệnh tiểu đường tu&yacute;p 2.</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Mỗi ng&agrave;y uống nước cam &eacute;p đều đặn sẽ l&agrave;m giảm sự sưng vi&ecirc;m trong cơ thể.</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px; text-align: center;"><img style="max-width: 100%; height: 374px; vertical-align: middle; border: 0px; width: 500px;" src="http://media.bizwebmedia.net/Sites/98268/data/upload/1379667482_chinh_20111089122407_0.jpg?0" alt="" /></p>', '54000', 40000, 'nuoc-cam-ep', '2016-05-07 11:22:20', 2, 1),
(32, 'Dâu Tây Ép', '387453-svetik.jpg', '<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Những quả d&acirc;u t&acirc;y với m&agrave;u đỏ v&agrave; ch&iacute;n mọng tr&ocirc;ng thật hấp dẫn phải kh&ocirc;ng.</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Ch&uacute;ng ta thường sử dụng d&acirc;u t&acirc;y để l&agrave;m b&aacute;nh như b&aacute;nh ngọt, b&aacute;nh quy...</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Ngo&agrave;i ra c&ograve;n d&ugrave;ng để trang tr&iacute; c&ugrave;ng m&oacute;n salad hay đặt một miếng d&acirc;u t&acirc;y v&agrave;o ly kem.</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">N&oacute; cũng l&agrave;m cho ly kem hấp dẫn hơn nhiều đấy.</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Nước &eacute;p d&acirc;u t&acirc;y c&oacute; kh&aacute; nhiều t&aacute;c dụng đối với sức khỏe của con người.</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Tuy nhi&ecirc;n với những bạn sử dụng nước &eacute;p d&acirc;u t&acirc;y hay bất kỳ đồ uống n&agrave;o kh&aacute;c cũng cần phải ngăn kh&ocirc;ng cho đường v&igrave; n&oacute; sẽ l&agrave;m giảm chất dinh dưỡng trong nước &eacute;p d&acirc;u t&acirc;y đi đấy nh&eacute;.</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px; text-align: center;"><img style="max-width: 100%; height: 500px; vertical-align: middle; border: 0px; width: 500px;" src="http://media.bizwebmedia.net/Sites/68297/data/upload/515997905_467.jpg" alt="" /></p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">Một trong những chất dinh dưỡng quan trọng c&oacute; thể được t&igrave;m thấy trong d&acirc;u t&acirc;y l&agrave; mangan . Điều n&agrave;y gi&uacute;p giảm vi&ecirc;m tế b&agrave;o v&agrave; gi&uacute;p ngăn ngừa cơn đau tim v&agrave; mang xuống nguy cơ bệnh tim mạch c&oacute; li&ecirc;n quan kh&aacute;c .</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">D&acirc;u t&acirc;y cũng l&agrave; một chất chống oxy h&oacute;a tự nhi&ecirc;n tốt . Hơn nữa, họ c&oacute; rất nhiều vitamin C gi&uacute;p trong việc chiến đấu với bệnh cảm lạnh th&ocirc;ng thường theo m&ugrave;a v&agrave; nhiều bệnh kh&aacute;c. D&acirc;u t&acirc;y cũng l&agrave; một c&aacute;ch hiệu quả để th&uacute;c đẩy hệ thống miễn dịch của bạn gi&uacute;p bạn lu&ocirc;n khỏe mạnh đấy</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px;">D&acirc;u t&acirc;y cũng gi&uacute;p bạn giảm c&acirc;n cực hiệu quả đấy nh&eacute;</p>\r\n<p style="margin: 0px 0px 20px; font-family: Arial, Verdana, sans-serif; font-size: 14px; text-align: center;"><img style="max-width: 100%; height: 750px; vertical-align: middle; border: 0px; width: 500px;" src="http://media.bizwebmedia.net/Sites/98268/data/upload/58520854bf65b03bbfc36359f6781022635362634918264039.jpg?0" alt="" /></p>', '60000', 55000, 'dau-tay-ep', '2016-05-07 11:23:35', 2, 1);

